INSERT INTO `kp_information_column` VALUES ('33', '配件营销', '埃克森美孚化工推出TPV新品', '立配网', '', '立配网', '已发布', '2011-11-15 20:28:02', '', '<p style=\"text-align:center;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" coordsize=\"21600,21600\" path=\"m@4@5l@4@11@9@11@9@5xe\" filled=\"f\" stroked=\"f\" o:spt=\"75\" o:preferrelative=\"t\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path gradientshapeok=\"t\" o:extrusionok=\"f\" o:connecttype=\"rect\"></v:path><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028091454_80819.jpg\" /></span></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">近日，埃克森美孚化工推出了山都平牌<span lang=\"EN-US\">121-XXM200</span>热塑性硫化弹性体<span lang=\"EN-US\">(TPV)</span>，这种弹性体具有良好的流动性，可增强汽车零件的美观效果，同时也能提高加工性能。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　据埃克森美孚化工公司山都平热塑性硫化弹性体全球品牌经理麦克·卢瑟介绍，该种弹性体具有良好的加工性，根据零件尺寸和壁厚的不同，可使注射压力减小<span lang=\"EN-US\">30%~40%</span>，使注射温度降低<span lang=\"EN-US\">10</span>℃，并且还有可能缩短加工时间。同时，这种热塑性硫化弹性体可以完全回收利用，从而降低汽车的生产成本和汽车能耗，具有显著的可持续性优势。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">据了解，山都平<span lang=\"EN-US\">121-XXM200</span>热塑性硫化弹性体提供了与三元乙丙橡胶不相上下的压缩和拉伸永久变形性能，并符合汽车零部件对耐紫外线性能的要求。新型热塑性硫化弹性体的注射成型压力较低，在成型过程中对流动方向的敏感性也要求不高。因此，发生零件扭曲的风险较小，加工起来更方便，模具也易于设计。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p></o:p></span></p>', '埃克森美孚', '埃克森美孚', '99', '2011-10-28 15:15:31', '2011-11-15 20:28:02', '2011-11-15 00:00:00');
INSERT INTO `kp_information_column` VALUES ('34', '厂商报道', '博世飞思卡尔合力研发芯片', '立配网', '', '', '已发布', '2011-10-28 15:37:21', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028091640_94033.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">据外媒消息，博世汽车电子研发部和飞思卡尔半导体将携手制作一个汽车安全气囊参考平台。提高新兴市场如印度和中国的汽车安全性。<span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">新的安全气囊参考平台基于飞思卡尔的微控制器系列芯片和博世的专用标准产品（<span lang=\"EN-US\">ASSP</span>）系列芯片。博世和飞思卡尔将为各自负责的部分提供销售和技术支持。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">飞思卡尔高级副总裁兼销售总监<span lang=\"EN-US\"> Henri Richad</span>表示，这一新的安全气囊参考平台不仅质量非常的好而且很实用，价格低廉。可以帮助当地供应商减少设计风险。博世还透露，这一平台预设有演示软件。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">博世和飞思卡尔均表示将在印度和中国的飞思卡尔科技论坛上首先展示这一成果，然后在<span lang=\"EN-US\">2012</span>年第一季度推出。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '博世,飞思卡尔,安全气囊', '博世', '11', '2011-10-28 15:17:34', '2011-10-31 22:43:53', null);
INSERT INTO `kp_information_column` VALUES ('35', '厂商报道', '辉门为奔驰V6柴油机提供技术支持', '立配网', '', '', '已发布', '2011-10-28 15:37:25', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028091839_81042.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">辉门公司日前透露已同奔驰进行合作，为其<span lang=\"EN-US\">V6</span>柴油机提供<span lang=\"EN-US\">DuraBowl</span>活塞技术支持。<span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">该<span lang=\"EN-US\">DuraBowl</span>活塞技术可以提高<span lang=\"EN-US\">V6</span>柴油机的燃油效率，降低排放量，同时增加柴油机的输出功率。这项先进技术是辉门将在本月的法兰克福车展上展示的技术之一。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">辉门能源部副总裁<span lang=\"EN-US\">Rainer Jueckstock</span>透露，<span lang=\"EN-US\">DuraBowl</span>可以在循环的热负荷之下，增强柴油机的抗疲劳强度，是辉门专业技术的体现。</span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">辉门还将在今年<span lang=\"EN-US\">9</span>月的法兰克福车展上展示其先进的电晕点火系统（<span lang=\"EN-US\">ACIS</span>）、动力系统以及其它一些提高燃油经济的技术。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '辉门,奔驰,发动机', '辉门', '11', '2011-10-28 15:19:38', '2011-10-31 22:43:46', null);
INSERT INTO `kp_information_column` VALUES ('36', '厂商报道', '江森自控拟投巨资研发电池回收利用技术', '立配网', '', '', '已发布', '2011-10-28 15:37:27', '', '<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028092032_11154.jpg\" width=\"350\" />&nbsp;</span></o:p></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">据外媒消息，汽车零部件供应商江森自控（<span lang=\"EN-US\">JCI</span>）表示，拟向其墨西哥新莱昂州的电池回收利用中心投资<span lang=\"EN-US\">7000</span>万美元。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">江森自控能源部副总裁兼总经理艾伦<span lang=\"EN-US\">•</span>马丁（<span lang=\"EN-US\">Allen Martin</span>）表示，此次投资主要用于更新回收利用中心的设备，为其配备更大、更高效的旋转炉。目的是增强<span lang=\"EN-US\">JCI</span>在北美地区的电池回收能力，不断升级设备，研发出最先进的电池回收利用技术。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">JCI</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">表示其墨西哥工厂的回收利用设备的排放量刚好符合美国的标准，高于其它竞争对手的平均水平。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">江森自控能源部主要为整车厂和售后市场供应汽车电池，在封闭环、汽车电池回收利用等反面的技术也比较领先。<span lang=\"EN-US\">JCI</span>能源部有<span lang=\"EN-US\">35</span>个工厂，提供全球所需铅酸蓄电池的<span lang=\"EN-US\">1/3</span>以上。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '江森自控', '江森自控', '11', '2011-10-28 15:20:52', '2011-10-31 22:43:39', null);
INSERT INTO `kp_information_column` VALUES ('37', '厂商报道', '麦格纳卡斯马收购格林威尔铸件公司', '立配网', '', '', '已发布', '2011-10-28 15:37:30', '', '<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028092149_95992.jpg\" width=\"350\" /></span></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">据外媒消息，麦格纳子公司卡斯马国际（</span><span style=\"font-size:14px;\" lang=\"EN-US\">Cosma International</span><span style=\"font-size:14px;\">）日前宣布已收购格林威尔铸件公司（</span><span style=\"font-size:14px;\" lang=\"EN-US\">Grenville Castings Ltd.,</span><span style=\"font-size:14px;\">）。</span><span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">Grenville Castings</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">公司总部位于安大略省，主要开发复杂的机构铸件，其核心技术就是薄壁低压铸件。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">麦格纳卡斯马公司是麦格纳国际的子公司，产品业务广泛，包括金属车身系统、零部件、装配以及组件等，在全球有</span><span style=\"font-size:14px;\" lang=\"EN-US\">47</span><span style=\"font-size:14px;\">家工厂和</span><span style=\"font-size:14px;\" lang=\"EN-US\">25</span><span style=\"font-size:14px;\">个产品研发中心。</span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:11.0pt;mso-ascii-theme-font:minor-fareast;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">卡斯马国际公司广阔的业务范围再加上<span style=\"font-size:14px;\" lang=\"EN-US\">Grenville Castings</span>公司专业的技术，将使其成为结构铸件方面全球领先的供应商，此次收购也将有助于<span style=\"font-size:14px;\" lang=\"EN-US\">2014</span>年卡斯马国际将要开发的车辆底盘项目。</span></p>', '麦格纳,卡斯马,格林威尔', '卡斯马', '11', '2011-10-28 15:24:18', '2011-10-31 22:43:33', null);
INSERT INTO `kp_information_column` VALUES ('38', '厂商报道', '美驰将在江苏建立现代化零配件物流配送中心', '立配网', '', '', '已发布', '2011-10-28 15:56:28', '', '<p align=\"center\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028095537_95834.jpg\" width=\"350\" /></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">据美通社</span><span style=\"font-size:14px;\" lang=\"EN-US\">8</span><span style=\"font-size:14px;\">月</span><span style=\"font-size:14px;\" lang=\"EN-US\">23</span><span style=\"font-size:14px;\">日的报道，在</span><span style=\"font-size:14px;\" lang=\"EN-US\">2011</span><span style=\"font-size:14px;\">中国国际工程机械配套件博览会上，美驰公司宣布计划在江苏徐州建立一个占地</span><span style=\"font-size:14px;\" lang=\"EN-US\">2300</span><span style=\"font-size:14px;\">平方米的售后零配件物流配送中心。这一全新的物流配送中心将坐落于美驰在中国的非公路业务合资子公司</span><span style=\"font-size:14px;\" lang=\"EN-US\">——</span><span style=\"font-size:14px;\">徐州美驰车桥有限公司（徐州美驰）投建的新厂房内，预计将于</span><span style=\"font-size:14px;\" lang=\"EN-US\">2012</span><span style=\"font-size:14px;\">年</span><span style=\"font-size:14px;\" lang=\"EN-US\">2</span><span style=\"font-size:14px;\">月正式交付使用。</span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:11.0pt;mso-ascii-theme-font:minor-fareast;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">徐州物流配送中心建成后，将作为美驰在中国的非公路售后零配件运营中枢，为中国非公路售后市场提供多种传动系统部件、底盘部件及悬架部件产品。该中心将运用现代化的仓储和物流体系，采用场所管理和备货作业模式，大大增加售后配件的备货品种和库存量；配备全自动化的包装和印刷设备，全面提升配件的生产效率；采用现代化信息管理系统，通过网络将企业与各工厂、物流中心及经营最先端的分销商连接起来，使订货信息得以及时传递，有效提高订单交付的速度和效率。此外，徐州物流配送中心还将配备独立的采购、销售、客服、质量和仓储管理等专职人员，确保从订货、生产、检验到发货的每一个环节准确无误，最终提高客户满意度。</span></p>', '美驰,物流配送', '美驰', '11', '2011-10-28 15:56:24', '2011-10-31 22:43:26', null);
INSERT INTO `kp_information_column` VALUES ('39', '厂商报道', '舍弗勒三期项目明年2月投产', '立配网', '', '', '已发布', '2011-11-15 20:28:14', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-size:10.5pt;\"><span style=\"font-size:small;\">舍弗勒（中国）有限公司三期项目正在江苏太仓加紧建设。据介绍，舍弗勒（中国）有限公司三期项目总规划面积约<span lang=\"EN-US\">8.8</span>万平方米，建设项目包括两个厂房，即舍弗勒（中国）有限公司四厂和五厂。四厂厂房规划建筑面积<span lang=\"EN-US\">3.87</span>万平方米，五厂厂房规划建筑面积<span lang=\"EN-US\">2</span>万余平方米。该项目自今年<span lang=\"EN-US\">4</span>月<span lang=\"EN-US\">28</span>日奠基以来，工程施工进展顺利，目前桩基工程已全部完成，四厂厂房正在安装钢结构。按照目前的建设进度，预计四厂厂房将在今年年底前建成完工，计划于<span lang=\"EN-US\">2012</span>年<span lang=\"EN-US\">2</span>月投产。</span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-size:10.5pt;\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028095747_77060.jpg\" width=\"350\" /></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-size:10.5pt;\">自<span lang=\"EN-US\">1995</span>年在太仓投资生产以来，舍弗勒集团大中华区逐步拓展生产与销售业务，<span lang=\"EN-US\">2010</span>年销售额与<span lang=\"EN-US\">2009</span>年相比增长了<span lang=\"EN-US\">45%</span>以上，太仓工厂也成为舍弗勒集团在全球范围内最大的生产基地之一。随着公司经营管理水平、生产技术手段及市场拓展能力的不断提升，集团总部对大中华区抱有更高的期望，决定对大中华区进一步加大投资力度，于是确定上马三期项目。项目全部建成后，这里将是一个技术先进、设备一流、具有国际水准的现代化工厂。<span lang=\"EN-US\">”</span>舍弗勒（中国）有限公司有关负责人透露了舍弗勒集团斥资<span lang=\"EN-US\">7200</span>万欧元建设新项目的背景。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-size:10.5pt;\"><span style=\"font-size:small;\">据了解，舍弗勒（中国）有限公司四厂和五厂投产后，将主要从事精密轴承、汽车重要零部件的生产业务，产品销往中国乃至亚太区。三期项目将以新技术为支撑，促进企业升级。据悉，太仓新工厂将引入世界最先进的生产技术，集中生产滚针轴承，具有高技术含量的各种中、大尺寸轴承，先进的汽车轮毂轴承，以及变速箱和发动机的关键零部件等。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-size:10.5pt;\"><span style=\"font-size:small;\">舍弗勒（中国）有限公司三期项目建成投产后，舍弗勒将为太仓创造<span lang=\"EN-US\">500~1000</span>个新的就业岗位，并将引进企业管理、项目管理、质量管理及产品研发等方面的高端人才。舍弗勒太仓工厂到<span lang=\"EN-US\">“</span>十二五<span lang=\"EN-US\">”</span>末，预计总资产可达<span lang=\"EN-US\">40</span>亿元，占地面积<span lang=\"EN-US\">345</span>亩，将实现年产值超<span lang=\"EN-US\">60</span>亿元。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-size:10.5pt;\">对舍弗勒集团来说，三期项目建成后，将有助于系统地提升舍弗勒在中国的生产运营能力，以更好地满足中国市场乃至亚太市场日益增长的产品需求，进一步巩固舍弗勒在行业的领先地位，对舍弗勒集团全球业务的快速扩张具有重要的战略意义。<span lang=\"EN-US\">”</span>舍弗勒（中国）有限公司有关负责人说。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '舍弗勒', '舍弗勒', '93', '2011-10-28 15:57:58', '2011-11-15 20:28:14', '2011-11-08 00:00:00');
INSERT INTO `kp_information_column` VALUES ('40', '厂商报道', '特瑞堡上海新研发中心开业', '立配网', '', '', '已发布', '2011-10-28 15:59:52', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">综合外电报道，瑞典汽车零部件供应商特瑞堡公司（<span lang=\"EN-US\">Trelleborg Automotive</span>）于<span lang=\"EN-US\">6</span>月<span lang=\"EN-US\">20</span>日宣布，该公司在上海的新</span><span lang=\"EN-US\"><a href=\"http://auto.gasgoo.com/searchnews/keywords/%e7%a0%94%e5%8f%91\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">研发</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">中心正式开业。据悉，该中心将负责开发最先进的汽车用降噪、减震产品。</span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028095919_51458.jpg\" width=\"350\" /></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">特瑞堡汽车业务区域总裁<span lang=\"EN-US\">Jim Law </span>表示：<span lang=\"EN-US\">“</span>中国汽车行业正在不断发展，对特瑞堡来说，最重要的就是要与客户站在一起，客户在哪里，我们就会在哪里出现。<span lang=\"EN-US\">”</span>他称，<span lang=\"EN-US\">“</span>每种车型都需要不同的解决方案，新的研发中心可以向中国客户提供定制的测试选择和咨询服务。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">同时，该公司还表示，中国是特瑞堡集团最重要的市场之一。目前，该公司在中国拥有<span lang=\"EN-US\">6</span>家全资生产工厂，合计拥有员工<span lang=\"EN-US\">1700</span>多人。<span lang=\"EN-US\">2010</span>年该公司在华年销售净额接近<span lang=\"EN-US\">10</span>瑞典克朗（约合<span lang=\"EN-US\">1.56</span>亿美元）。</span></span><span lang=\"EN-US\"><o:p></o:p></span></p>', '特瑞堡', '特瑞堡', '1', '2011-10-28 15:59:47', '2011-10-31 22:46:27', null);
INSERT INTO `kp_information_column` VALUES ('41', '厂商报道', '霍尼韦尔与玉柴签署长期战略合作协议', '立配网', '', '', '已发布', '2011-10-28 16:05:23', '', '<p style=\"line-height:125%;text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><!--?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /--><st1:chsdate year=\"2011\" month=\"9\" day=\"19\" islunardate=\"False\" isrocdate=\"False\" w:st=\"on\"><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\">2011</span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">年</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\">9</span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">月</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\">19</span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">日</span></st1:chsdate><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">，中国上海——全球领先的汽车涡轮增压器制造商霍尼韦尔公司（纽交所交易代码：</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\">HON</span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">）与中国境内知名柴油机制造商广西玉柴机械有限公司签署了长期战略合作协议，就进一步展开更为深远和广阔的合作达成共识，</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\"> </span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">借助霍尼韦尔的顶级涡轮增压器技术，携手研发高性能柴油机。</span></p>\r\n<p style=\"line-height:125%;text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\" align=\"center\"><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028100124_20316.jpg\" width=\"400\" /></span></p>\r\n<span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p> <p style=\"line-height:125%;text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">依据协议，双方决议通过各自的技术专长、品质优势和市场经验，建立长期互惠的合作关系。霍尼韦尔将向玉柴提供其尖端涡轮技术，助其研发高性能柴油机，增强玉柴产品在性能和成本方面的核心竞争力。</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"line-height:125%;text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><o:p>&nbsp;</o:p></span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">霍尼韦尔交通运输集团总裁兼首席执行官伊仕敏（</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\">Alex Ismail </span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">）坦言：“这份长期战略合作协议为霍尼韦尔涡轮增压技术部门和玉柴的进一步合作奠定了坚实基础。这一合作也凸显了霍尼韦尔致力于显著提高汽车燃油效率、降低尾气排放的自信和决心。玉柴是霍尼韦尔涡轮增压技术部的一个重要战略业务合作伙伴，我们非常看好双方的互利合作前景。”</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"line-height:125%;text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><o:p>&nbsp;</o:p></span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">此外，玉柴和霍尼韦尔还将联手开发面向中国市场的涡轮增压器产品。</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"line-height:125%;text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><o:p>&nbsp;</o:p></span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">玉柴董事长<st1:personname w:st=\"on\" productid=\"晏平\">晏平</st1:personname>先生表示：“作为国内产品型谱最全的内燃机制造基地和中小型全液压挖掘机制造商，玉柴一直致力于为海内外用户提供最满意的产品和服务。我们与霍尼韦尔的合作已经有十多年的历史了，且非常愉快，此次战略协议的签署将有助于霍尼韦尔的先进技术与玉柴的本土市场经验的进一步融合，从而促进双方在未来几年内获得持续稳健的发展。”</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"line-height:125%;text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><span style=\"mso-spacerun:yes;\">&nbsp;</span></span><span style=\"line-height:125%;font-family:宋体;font-size:12pt;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;\">霍尼韦尔涡轮增压技术部是霍尼韦尔交通系统集团旗下的一个业务分支。作为领先的汽车供应商，霍尼韦尔交通系统集团专注于通过领先的技术、全球一流的品牌和为全球汽车客户定制的解决方案来提高车辆性能、效率和外观。</span><span style=\"line-height:125%;font-family:\'Arial\',\'sans-serif\';font-size:12pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"line-height:125%;text-indent:24pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\">&nbsp;</p>\r\n</o:p></span>', '霍尼韦尔,玉柴', '霍尼韦尔,玉柴', '11', '2011-10-28 16:01:52', '2011-10-31 22:43:15', null);
INSERT INTO `kp_information_column` VALUES ('42', '厂商报道', '德尔福在法兰克福车展展示防碰撞技术', '立配网', '', '', '已发布', '2011-10-28 16:05:28', '', '<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\"><span style=\"font-size:small;\">据外媒消息，德尔福拟在下个月的法兰克福车展上展示其创新的可防止碰撞技术，这一最新科技将有效的保护消费者的安全。</span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\"><span style=\"font-size:small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028100304_86834.jpg\" width=\"350\" height=\"233\" /></span></span></p>\r\n<p style=\"text-align:center;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"center\">&nbsp;</p>\r\n<span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\"> <p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">德尔福发明了最新的传感器</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">RACam</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">，</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">RACam</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">结合了雷达和摄像机系统，可以进行一些列的安全保护措施，比如自动控速、照明、识别交通信号、碰撞警告、检测行人和自动刹车等，可以在司机没有意识到危险的情况下自动减速或者刹车。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">德尔福欧洲区总裁</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">Michael Gassen</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">表示，汽车行业在被动安全系统方面已经非常成功，有效地降低了碰撞的严重性，但是为了进一步提高道路安全，汽车制造商应该积极研发防碰撞技术，从根本上避免碰撞的发生。德尔福的防碰撞技术将在下个月的法兰克福车展上亮相，在</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">2014</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">年上市。</span></span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span>&nbsp;</p>\r\n</span>', '德尔福', '德尔福', '21', '2011-10-28 16:05:18', '2011-10-31 22:42:05', null);
INSERT INTO `kp_information_column` VALUES ('43', '厂商报道', '曼胡默尔塑料机油模块产量突破500万', '立配网', '', '', '已发布', '2011-10-28 16:08:56', '', '<p style=\"text-indent:24pt;mso-char-indent-count:2.0;\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">在公司成立<span lang=\"EN-US\">70</span>周年之际，曼胡默尔又迎来<span lang=\"EN-US\"><a href=\"http://www.plas.hc360.com/\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">塑料</span></span></span></a></span>机油模块产量高达<span lang=\"EN-US\">500</span>万的里程碑时刻。八年前，曼胡默尔在<span lang=\"EN-US\">IAA</span>法兰克福车展上推出全球首款塑料机油模块，成为关注的焦点，在<span lang=\"EN-US\"><a href=\"http://www.auto.hc360.com/\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">汽车</span></span></span></a></span>行业中引起轰动。该产品由曼胡默尔与奥迪公司共同合作开发，最初用于大众和奥迪的新型二升四缸<span lang=\"EN-US\">FSI</span>直喷汽油<span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/zt/fdj/index.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">发动机</span></span></span></a></span>。如今，曼胡默尔塑料机油模块生产量已突破<span lang=\"EN-US\">500</span>万大关，其中<span lang=\"EN-US\">400</span>万在曼胡默尔德国总部路德维希堡完成。同时，针对不同客户需求开发的新型模块也已完成。现在，曼胡默尔路德维希堡工厂生产的塑料机油模块已遍布欧洲各国。与此同时，曼胡默尔已推出第三代复合集成机油模块，同时产量也得到了大幅提高。除了路德维希堡总部，曼胡默尔在巴西和捷克共和国的工厂也在生产塑料机油模块产品。<span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"text-align:center;\" align=\"center\"><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028100627_34790.jpg\" width=\"350\" /></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 曼胡默尔集团总裁兼首席执行官<span lang=\"EN-US\">AlfredWeber</span>先生表示：<span lang=\"EN-US\">“</span>毫无疑问，塑料机油模块是车用塑料加工的一座里程碑，也是我们员工创新能力的最佳证明。正如德国生产<span lang=\"EN-US\">400</span>万塑料机油模块所显示的那样，它不仅彻底改变了发动机机油管理模式，也成为路德维希堡出口产品的成功典范。像塑料机油模块这样的创新产品为我们今年的生产庆典和公司成立<span lang=\"EN-US\">70</span>周年庆典增色不少。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 除了提供品种多样的产品，曼胡默尔还将业务从德国扩展到欧洲，乃至全世界。到<span lang=\"EN-US\">1959</span>年，曼胡默尔已在阿根廷和巴西开拓市场；<span lang=\"EN-US\">1965</span>年，曼胡默尔进入西班牙；自<span lang=\"EN-US\">1994</span>年起，曼胡默尔在美国市场上表现活跃；<span lang=\"EN-US\">1996</span>年，公司进入亚洲市场。如今，在全球超过<span lang=\"EN-US\">40</span>个地区都有曼胡默尔的身影，而且其规模继续扩大，从昔日小小的施瓦本零部件制造商拓展成为当今国际知名的系统供应商和汽车及工业过滤行业的市场领导者。曼胡默尔售后品牌<span lang=\"EN-US\">MANN-FILTER</span>（曼牌滤清器）是全球市场上最具价值和最强大的过滤器品牌之一。去年，曼胡默尔生产了<span lang=\"EN-US\">4</span>亿多个滤芯，这意味着每秒钟就有超过<span lang=\"EN-US\">25</span>个新滤芯被生产出来。近几年的数据统计显示，全球约<span lang=\"EN-US\">10</span>亿辆汽车曾多次使用或安装过曼胡默尔生产的滤清产品。曼胡默尔在其他新兴业务领域也十分活跃，比如水处理领域。在不久的将来，曼胡默尔还将继续拓展其它业务，不断缔造新的发展里程碑。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '曼胡默尔', '曼胡默尔', '90', '2011-10-28 16:06:51', '2011-10-31 22:34:04', null);
INSERT INTO `kp_information_column` VALUES ('44', '厂商报道', '橡胶短缺,普利司通宣布库存不足', '立配网', '', '', '已发布', '2011-10-28 16:08:59', '', '<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 据外媒消息，普利司通轮胎日前告知整车厂因橡胶短缺，今年无法供应足够的轮胎。普利司通发言人透露，今年<span lang=\"EN-US\">7</span>月至<span lang=\"EN-US\">12</span>月，将有<span lang=\"EN-US\">50</span>万个（今年全部订单的<span lang=\"EN-US\">5%</span>）轮胎短缺。 <span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028100822_84885.jpg\" width=\"350\" /></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 据报道，三菱化学公司的子公司日本聚丙烯公司也存在同样的问题，因日本<span lang=\"EN-US\">3</span>月份的地震，其鹿岛工厂被迫关闭两个月，而工厂的常规维修是在<span lang=\"EN-US\">10</span>月份，所以导致生产<span lang=\"EN-US\"><a href=\"http://www.search.hc360.com/cgi-bin/seinterface.cgi?word=保险杠\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">保险杠</span></span></span></a></span>所需要的树脂库存不足。</span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;\">日本<span lang=\"EN-US\"><a href=\"http://www.auto.hc360.com/\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">汽车</span></span></span></a><a href=\"http://info.qipei.hc360.com/list/qyjj.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">制造商</span></span></span></a></span>协会（<span lang=\"EN-US\">JAMA</span>）主席志贺俊之表示，由于三月份的日本地震，日本整车厂目前都在急剧扩大产量用以弥补<span lang=\"EN-US\">3</span>月份至<span lang=\"EN-US\">5</span>月份的损失，<span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/qyjj.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">供应商</span></span></span></a></span>也准备在今年后半年扩大产量。</span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;\">日本整车厂<span lang=\"EN-US\">5</span>月份产量同比下降<span lang=\"EN-US\">30.9%</span>，<span lang=\"EN-US\">6</span>月份产量同比下降<span lang=\"EN-US\">14%</span>，七月份产量同比下降<span lang=\"EN-US\">8.9%</span>。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '普利司通', '普利司通', '10', '2011-10-28 16:08:49', '2011-10-31 22:46:21', null);
INSERT INTO `kp_information_column` VALUES ('45', '厂商报道', '米其林正式入驻中国轮胎城', '立配网', '', '', '已发布', '2011-10-28 16:15:15', '', '<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp; 2011</span>年<span lang=\"EN-US\">8</span>月<span lang=\"EN-US\">27</span>日，中国轮胎城与全球轮胎业巨无霸米其林轮胎浙江省总<span lang=\"EN-US\"><a href=\"http://www.search.hc360.com/cgi-bin/seinterface.cgi?word=代理\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">代理</span></span></span></a></span>——杭州力德贸易有限公司在中国轮胎城举行<span lang=\"EN-US\"><a href=\"http://www.search.hc360.com/cgi-bin/seinterface.cgi?word=合作\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">合作</span></span></span></a></span>签约仪式，此次签约标志着连杭物流中国轮胎城在引进国际品牌、打造轮胎商贸流通航母的道路上迈出了坚实的一步。 <span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028101047_92891.jpg\" width=\"350\" /></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 浙江连杭物流有限公司董事长周尚华与杭州力德贸易有限公司董事长苏军一起出席了此次签约仪式。周董对米其林的入驻表示欢迎，他说，连杭物流是浙江省重点服务业项目，具有信息中心、产品交易区、货运专线区、仓储物流区、商务<span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/zt/chukou/index.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">配套</span></span></span></a></span>区，是目前最为先进的、完善的市场服务基地。产品交易区的中国轮胎城一直以来致力于引进国内外优质轮胎品牌，米其林这样国际知名品牌的入驻，对于中国轮胎城的品牌知名度、业界号召力以及后期招商的不断完善具有积极意义，同时他保证中国轮胎城及连杭物流经营团队一定会竭诚为杭州力德贸易有限公司做好支持与服务工作，最后他希望双方共同努力、加强合作，实现双赢。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 据悉，杭州力德贸易有限公司作为米其林浙江总代理，公司在八年来为浙江省带来轮胎品牌米其林、回力、百路驰等著名品牌的优质轮胎。目前，该公司年<span lang=\"EN-US\"><a href=\"http://www.biz.hc360.com/\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">销售</span></span></span></a></span>额突破<span lang=\"EN-US\">2</span>亿，并预计<span lang=\"EN-US\">2015</span>年达到年销售额超过<span lang=\"EN-US\">5</span>亿的规模。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '米其林', '米其林', '10', '2011-10-28 16:11:07', '2011-10-31 22:46:00', null);
INSERT INTO `kp_information_column` VALUES ('46', '厂商报道', '庞大集团获萨博汽车、零部件中国独家总经销权', '立配网', '', '', '已发布', '2011-10-28 16:15:11', '', '<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp; 2011</span>年<span lang=\"EN-US\">8</span>月<span lang=\"EN-US\">31</span>日，庞大集团发布了<span lang=\"EN-US\">“</span>收购<span lang=\"EN-US\">(</span>萨博<span lang=\"EN-US\">)</span>资产后续进展情况公告<span lang=\"EN-US\">”</span>。公告显示：庞大集团下属全资子公司北京中冀乐业<span lang=\"EN-US\"><a href=\"http://www.auto.hc360.com/\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">汽车</span></span></span></a><a href=\"http://www.biz.hc360.com/\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">销售</span></span></span></a></span>有限公司<span lang=\"EN-US\">(</span>以下简称<span lang=\"EN-US\">“</span>中冀乐业<span lang=\"EN-US\">”)</span>于<span lang=\"EN-US\">2011</span>年<span lang=\"EN-US\">8</span>月<span lang=\"EN-US\">30</span>日与萨博汽车、<span lang=\"EN-US\">SAAB Automobile Parts AB(</span>以下简称<span lang=\"EN-US\">“</span>萨博零部件<span lang=\"EN-US\">”)</span>签订了《总经销协议》，根据该协议的规定，萨博汽车与萨博零部件授予中冀乐业在中国<span lang=\"EN-US\">(</span>不包括香港特别行政区和台湾地区<span lang=\"EN-US\">)(</span>以下简称<span lang=\"EN-US\">“</span>地域<span lang=\"EN-US\">”)</span>对萨博车辆和零部件的独家总经销权。<span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:0cm;\" align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028101213_69143.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-indent:0cm;\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 《总经销协议》主要内容如下：<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;1</span>、萨博汽车委任中冀乐业担任在地域内<span lang=\"EN-US\"><a href=\"http://www.ceo.hc360.com/\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">营销</span></span></span></a></span>、销售、经销及服务标有、使用或携带萨博品牌的车辆<span lang=\"EN-US\">(</span>以下简称<span lang=\"EN-US\">“</span>萨博车辆<span lang=\"EN-US\">”)</span>、附属产品和衍生产品<span lang=\"EN-US\">(</span>合称<span lang=\"EN-US\">“</span>萨博车辆产品<span lang=\"EN-US\">”)</span>的独家和排他性的批发商并对此授予总经销权，授权中冀乐业独家和排他地授权经销商在地域内营销、销售、经销及服务萨博车辆产品，并授权中冀乐业决定在地域内的经销商网络；<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp; 2</span>、萨博零部件委任中冀乐业担任在地域内营销、销售、经销及服务为萨博车辆制造的所有零部件及配件<span lang=\"EN-US\">(</span>以下简称<span lang=\"EN-US\">“</span>零部件<span lang=\"EN-US\">”)</span>、附属产品和衍生产品<span lang=\"EN-US\">(</span>合称<span lang=\"EN-US\">“</span>萨博零部件产品<span lang=\"EN-US\">”)</span>的独家和排他性的批发商并对此授予总经销权，授权中冀乐业独家和排他地授权经销商在地域内营销、销售、经销及服务萨博零部件产品，并授权中冀乐业决定在区域内的经销商网络；<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp; 3</span>、针对所有进口和中国国产的萨博车辆以及成长品牌的车辆，按零售网点数量计算，经销商网络应在庞大汽贸集团股份有限公司<span lang=\"EN-US\">(</span>以下简称<span lang=\"EN-US\">“</span>庞大<span lang=\"EN-US\">”)</span>的经销商和独立经销商之间按<span lang=\"EN-US\">50-50</span>的比例划分；<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp; 4</span>、在中冀乐业向中国国家工商行政管理总局和商务部备案为总经销商后，中冀乐业应组织和负责地域内的萨博车辆、零部件、附属产品和衍生产品<span lang=\"EN-US\">(</span>合称<span lang=\"EN-US\">“</span>产品<span lang=\"EN-US\">”)</span>建立和维持一个由多个服务中心组成的网络，用以提供高效而全面的服务和维修，无论产品是否为中冀乐业所售。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '萨博,中冀乐业', '萨博', '10', '2011-10-28 16:12:53', '2011-10-31 22:46:06', null);
INSERT INTO `kp_information_column` VALUES ('47', '厂商报道', '“威伯科”为现代汽车提供紧急制动技术', '立配网', '', '', '已发布', '2011-10-28 16:15:06', '', '<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; 全球商用</span><span lang=\"EN-US\"><a href=\"http://www.auto.hc360.com/\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\"><span style=\"font-size:14px;\">汽车</span></span></span></span></a></span><span style=\"font-size:14px;\">安全及控制系统技术一级</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/qyjj.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\"><span style=\"font-size:14px;\">供应商</span></span></span></span></a></span><span style=\"font-size:14px;\">威伯科日前宣布，已与韩国最大的</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/syc_cpcs.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\"><span style=\"font-size:14px;\">商用车</span></span></span></span></a></span><span style=\"font-size:14px;\">生产商现代汽车公司建立长期</span><span lang=\"EN-US\"><a href=\"http://www.search.hc360.com/cgi-bin/seinterface.cgi?word=合作\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\"><span style=\"font-size:14px;\">合作</span></span></span></span></a></span><span style=\"font-size:14px;\">关系。威伯科将为现代汽车公司开发和提供一项具突破性的自动紧急制动技术。 </span></span></span></p>\r\n<p align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028101428_29749.jpg\" width=\"350\" /></o:p></span></span></span></p>\r\n<p><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span style=\"font-size:14px;\" lang=\"EN-US\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:14px;\">据了解，该技术是先进的紧急制动系统（</span><span style=\"font-size:14px;\" lang=\"EN-US\">AEBS</span><span style=\"font-size:14px;\">）。在车辆即将发生碰撞时，会自动启动紧急制动程序，最大程度地减速，直至车辆完全停止，从而有效减少车辆因前方行驶车辆急停而产生的碰撞风险。此项技术提供驾驶员听觉和视觉双重预警且对于处于静止状态的车辆同样作出反应，例如身处交通大拥堵时。</span></span></span></p>\r\n<p><span style=\"font-family:\'Calibri\',\'sans-serif\';font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; </span>&nbsp;</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">欧盟预计从</span><span style=\"font-family:\'Calibri\',\'sans-serif\';font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">2013</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">年</span><span style=\"font-family:\'Calibri\',\'sans-serif\';font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">11</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">月起将在其</span><span style=\"font-family:\'Calibri\',\'sans-serif\';font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">27</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">个成员国的重型商用车上强制安装先进的紧急制动系统（</span><span style=\"font-family:\'Calibri\',\'sans-serif\';font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">AEBS</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">），而该技术将成为首个安装的系统。根据联合国欧洲经济委员会车辆制动相关的</span><span style=\"font-family:\'Calibri\',\'sans-serif\';font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">13</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">号文件规定，先进的紧急制动系统（</span><span style=\"font-family:\'Calibri\',\'sans-serif\';font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">AEBS</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">）还有望在韩国及全球另外</span><span style=\"font-family:\'Calibri\',\'sans-serif\';font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">29</span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-size:11.0pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">个国家的商用车上强制安装。</span></p>', '威伯科', '威伯科', '10', '2011-10-28 16:14:58', '2011-10-31 22:46:14', null);
INSERT INTO `kp_information_column` VALUES ('48', '厂商报道', '倍耐力拟扩展罗马尼亚工厂轮胎业务', '立配网', '', '', '已发布', '2011-10-28 16:16:36', '', '<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 倍耐力日前宣布开始扩展其罗马尼亚斯拉蒂纳工厂的轮胎生产业务。</span></span></p>\r\n<p align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028101615_89221.jpg\" width=\"350\" /></o:p></span></span></span></p>\r\n<p><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\">&nbsp;&nbsp;&nbsp; 2005</span>年至<span lang=\"EN-US\">2010</span>年期间，倍耐力已向其斯拉蒂纳的轮胎工、钢帘线工厂以及另外一个微粒过滤器工厂投资<span lang=\"EN-US\">3</span>亿欧元（<span lang=\"EN-US\">4.34</span>亿美元），截止到<span lang=\"EN-US\">2014</span>年，倍耐力的投资将增加到<span lang=\"EN-US\">4.5</span>亿欧元。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 这<span lang=\"EN-US\">4.5</span>亿欧元中，有<span lang=\"EN-US\">1.6</span>亿欧元用来扩建其罗马尼亚的轮胎工厂，扩建项目<span lang=\"EN-US\">2008</span>年已经开始，<span lang=\"EN-US\">2013</span>年结束，目的是在于扩大产能、提高倍耐力在欧洲市场的竞争力。该扩建项目得到罗马尼亚政府<span lang=\"EN-US\">2,800</span>万欧元的财政支持。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;斯拉蒂纳轮胎工厂今年的年产量预计将达到<span lang=\"EN-US\">700</span>万个，扩建项目结束之后将达到<span lang=\"EN-US\">1,000</span>万个，自称为最先进的轮胎<span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/qyjj.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">制造商</span></span></span></a></span>的斯拉蒂纳轮胎工厂将成为倍耐力最大的轮胎制造厂。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '倍耐力', '倍耐力', '10', '2011-10-28 16:16:30', '2011-10-31 22:45:54', null);
INSERT INTO `kp_information_column` VALUES ('49', '厂商报道', '博格华纳正在做新能源汽车相关产品', '立配网', '', '', '已发布', '2011-10-28 16:17:49', '', '<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;提高燃油经济性、降低排放、增强驾驶性能，是博格华纳公司的三大使命。翻开博格华纳百年以上的历史，我们清晰地发现，包括手动<span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/zt/bsq091009/index.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">变速器</span></span></span></a></span>、自动变速器、涡轮增压、全智能型四轮驱动，直到目前流行的双<span lang=\"EN-US\"><a href=\"http://www.search.hc360.com/cgi-bin/seinterface.cgi?word=离合器\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">离合器</span></span></span></a></span>自动变速器等，都是博格华纳最早开发或在世界上领先的产品和技术，这些也恰恰是博格华纳遵从三大使命所掌握的几大核心技术。</span></span></p>\r\n<p align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028101729_14294.jpg\" width=\"350\" /></o:p></span></span></span></p>\r\n<p><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 博格华纳与国内<span lang=\"EN-US\">12</span>家整车厂商联合成立的大连工厂，其所生产的双离合器自动变速器产品，不仅代表博格华纳的核心技术，而且据谈跃生介绍，世界上百分之六七十的各类自动变速器都要用到博格华纳在<span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/scp_zl.shtml\" target=\"_blank\"><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">摩擦材料</span></span></span></a></span>方面的核心底层技术。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-size:small;\"><span style=\"font-family:宋体;\">&nbsp;&nbsp;&nbsp; 今年<span lang=\"EN-US\">1-8</span>月，中国乘用车市场增长<span lang=\"EN-US\">34%</span>，而博格华纳公司的全球增长率是<span lang=\"EN-US\">55%</span>，中国区的增长速度更是国内乘用车市场增速的两倍。正是拥有独特的技术与产品，才使得博格华纳多年居于领先市场的地位。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>', '博格华纳', '博格华纳', '10', '2011-10-28 16:17:44', '2011-10-31 22:45:46', null);
INSERT INTO `kp_information_column` VALUES ('50', '厂商报道', '辉门为奔驰V6柴油机提供技术支持', '立配网', '', '', '已发布', '2011-11-04 16:34:34', '', '<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">辉门公司日前透露已同奔驰进行合作，为其</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">V6</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">柴油机提供</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">DuraBowl</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">活塞技术支持。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-no-proof:yes;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028101840_30835.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">该</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">DuraBowl</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">活塞技术可以提高</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">V6</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">柴油机的燃油效率，降低排放量，同时增加柴油机的输出功率。这项先进技术是辉门将在本月的法兰克福车展上展示的技术之一。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">辉门能源部副总裁</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">Rainer Jueckstock</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">透露，</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">DuraBowl</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">可以在循环的热负荷之下，增强柴油机的抗疲劳强度，是辉门专业技术的体现。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">辉门还将在今年</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">9</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">月的法兰克福车展上展示其先进的电晕点火系统（</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">ACIS</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">）、动力系统以及其它一些提高燃油经济的技术。</span></span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></p>', '辉门', '辉门', '10', '2011-10-28 16:35:04', '2011-11-04 16:34:34', null);
INSERT INTO `kp_information_column` VALUES ('58', '厂商报道', '博世飞思卡尔合力研发芯片', '立配网', '', '', '已发布', '2011-10-28 16:45:56', '', '<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">据外媒消息，博世汽车电子研发部和飞思卡尔半导体将携手制作一个汽车安全气囊参考平台。提高新兴市场如印度和中国的汽车安全性。新的安全气囊参考平台基于飞思卡尔的微控制器系列芯片和博世的专用标准产品（</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\">ASSP</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">）系列芯片。博世和飞思卡尔将为各自负责的部分提供销售和技术支持。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-no-proof:yes;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111028/20111028104219_72506.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">飞思卡尔高级副总裁兼销售总监</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"> Henri Richad</span><span style=\"font-family:宋体;color:black;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">表示，这一新的安全气囊参考平台不仅质量非常的好而且很实用，价格低廉。可以帮助当地供应商减少设计风险。博世还透露，这一平台预设有演示软件。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-bidi-font-size:10.5pt;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">博世和飞思卡尔均表示将在印度和中国的飞思卡尔科技论坛上首先展示这一成果，然后在</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">2012</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">年第一季度推出。</span>', '飞思卡尔', '飞思卡尔', '10', '2011-10-28 16:45:09', '2011-10-31 22:45:29', null);
INSERT INTO `kp_information_column` VALUES ('60', '厂商报道', '78家汽车及零部件公司净利润增长不到1成 ', '立配网', '', '立配网', '已发布', '2011-10-31 08:56:33', 'thumb_4eadf0b31ddb8.jpg', '<p><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">&nbsp;&nbsp;&nbsp; 截至</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">2011</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">年</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">8</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">月</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">28</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">日，有</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">78</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">家汽车及零部件行业上市公司发布了</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">2011</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">年中期报告，这些公司总计实现净利润</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">147</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">亿，同比增长</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">8%</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">。国内汽车行业经过了连续两年的高增长之后，业绩增幅明显放缓。</span></p>\r\n<p align=\"center\"><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031014921_36326.jpg\" width=\"350\" /></span></p>\r\n<span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\"> <p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">过去的两年里，在政策刺激、消费升级等因素的推动下，不少汽车公司连续实现高增长。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">2009</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">年，国内</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">21</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">家汽车整车生产商实现净利润总额</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">149.6</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">亿元，同比</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">2008</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">年的</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">32.2</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">亿元增长</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">4.6</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">倍。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">从今年上半年的情况看，一些大型整车企业业绩明显增长乏力。比如，笼罩着巴菲特光环的明星公司比亚迪</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\"> </span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">回归</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">A</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">股交出的第一份财务报告便呈现了业绩大滑坡，中期净利润仅</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">2.75</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">亿元，同比大降</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">88.63%</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">。一汽轿车</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\"> </span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">的业绩也不甚理想，上半年公司实现营业总收入</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">190.8</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">亿元，同比增长</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">6.53%</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">，净利润</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">8.04</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">亿元，同比下降</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">39.87%</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">今年上半年，国内汽车行业遇到了市场、资源等方面预期之外的变化。随着国家金融货币政策收紧、通胀加剧、汽车鼓励政策退出、油价上涨、北京限购等因素的影响，国内乘用车市场增速大幅下滑，自主产品受到较大冲击。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">另外，因价格下降，成本上涨及折旧增加等因素，不少汽车公司上半年盈利能力下降。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">业绩增幅靠前的汽车及零部件行业公司有亚星客车</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\"> </span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">、青岛双星</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\"> </span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">等，这些公司的业绩虽然看起来增幅很大，但都是由于去年基数较小所致，本身经营并无特别大的看点。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">在众多汽车及零部件行业公司里面，威孚高科</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\"> </span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">增长较为突出，业绩实实在在实现了逆势增长，威孚高科</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">2011</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">年上半年净利润同比增长</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">102%</span><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">。分析人士认为，该公司长期受益于柴油机排放升级。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">此外，从公布的数据来看，该类公司的现金流也有趋紧现象。去年这些公司经营活动产生的现金流量净额总额为</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">100</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">亿元，而今年中期，这个数字缩减至</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">53</span><span style=\"font-family:宋体;color:black;font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">亿元。数字背后，是汽车销售景气度的下滑。</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n</span>', '汽车行业', '汽车行业', '10', '2011-10-31 08:50:20', '2011-10-31 22:44:55', null);
INSERT INTO `kp_information_column` VALUES ('61', '厂商报道', '麦格纳卡斯马收购格林威尔铸件公司', '立配网', '', '立配网', '已发布', '2011-10-31 08:56:30', '', '<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">据外媒消息，麦格纳子公司卡斯马国际（</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">Cosma International</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">）日前宣布已收购格林威尔铸件公司（</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">Grenville Castings Ltd.,</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">）。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">Grenville Castings</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">公司总部位于安大略省，主要开发复杂的机构铸件，其核心技术就是薄壁低压铸件。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">麦格纳卡斯马公司是麦格纳国际的子公司，产品业务广泛，包括金属车身系统、零部件、装配以及组件等，在全球有</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">47</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">家工厂和</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">25</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">个产品研发中心。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">卡斯马国际公司广阔的业务范围再加上</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">Grenville Castings</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">公司专业的技术，将使其成为结构铸件方面全球领先的供应商，此次收购也将有助于</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">2014</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">年卡斯马国际将要开发的车辆底盘项目。</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '麦格纳卡斯马,收购', '汽车行业', '10', '2011-10-31 08:51:41', '2011-10-31 22:45:03', null);
INSERT INTO `kp_information_column` VALUES ('62', '厂商报道', '江森自控关闭与帅福得成立的合资公司', '立配网', '', '立配网', '已发布', '2011-10-31 08:56:27', 'thumb_4eadf19f1c6f3.jpg', '<p style=\"text-align:left;line-height:18pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:14px;\">据外媒消息，江森自控与帅福得日前表示关闭其合资公司。</span></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\"><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031015258_38452.jpg\" width=\"350\" /></span></span></p>\r\n<span style=\"font-family:宋体;color:black;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\"> <p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">2006</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">年江森自控与帅福得成立了合资公司，该合资公司主营锂离子电池的研发和生产。日前双方同意终止合作关系，关闭该合资公司。江森自控将收购帅福得持有的价值</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">1.45</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">亿美元的股票，另外江森自控需支付给帅福得在锂离子电池方面的专利技术转让费。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">该合资公司在法国的工厂归帅福得所有，除此之外的其它的资产全部归江森自控。这笔交易仍须获得相关管理部门的批准，预计在</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">9</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">月底最终达成。</span></span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><o:p></o:p></span>&nbsp;</p>\r\n</span>', '江森', '江森', '10', '2011-10-31 08:54:01', '2011-10-31 22:45:10', null);
INSERT INTO `kp_information_column` VALUES ('63', '厂商报道', '特瑞堡上海新研发中心开业', '立配网', '', '立配网', '已发布', '2011-10-31 08:56:24', '', '<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">综合外电报道，瑞典汽车零部件供应商特瑞堡公司（</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">Trelleborg Automotive</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">）于</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">6</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">月</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">20</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">日宣布，该公司在上海的新</span><span lang=\"EN-US\"><a href=\"http://auto.gasgoo.com/searchnews/keywords/%e7%a0%94%e5%8f%91\" target=\"_blank\"><span style=\"font-family:宋体;color:windowtext;text-decoration:none;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;text-underline:none;\" lang=\"EN-US\"><span style=\"font-size:14px;\" lang=\"EN-US\">研发</span></span></a></span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">中心正式开业。据悉，该中心将负责开发最先进的汽车用降噪、减震产品。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">特瑞堡汽车业务区域总裁</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">Jim Law </span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">表示：</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">中国汽车行业正在不断发展，对特瑞堡来说，最重要的就是要与客户站在一起，客户在哪里，我们就会在哪里出现。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">”</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">他称，</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\">每种车型都需要不同的解决方案，新的研发中心可以向中国客户提供定制的测试选择和咨询服务。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\">”<o:p></o:p></span></span></p>\r\n<p><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">&nbsp;&nbsp;&nbsp; 同时，该公司还表示，中国是特瑞堡集团最重要的市场之一。目前，该公司在中国拥有</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">6</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">家全资生产工厂，合计拥有员工</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">1700</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">多人。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">2010</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">年该公司在华年销售净额接近</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">10</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">瑞典克朗（约合</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">1.56</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">亿美元）。</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '汽车行业,特瑞堡', '特瑞堡', '10', '2011-10-31 08:54:52', '2011-10-31 22:45:16', null);
INSERT INTO `kp_information_column` VALUES ('64', '厂商报道', '斯凯孚（SKF）设立第二家大连轴承工厂', '立配网', '', '立配网', '已发布', '2011-10-31 08:56:21', '', '<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2011</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">年</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">9</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">月</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">26</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">日，大连——斯凯孚（</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">）在大连投资的第二家轴承工厂于今日正式举行开幕庆典。第一家工厂于</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">2006</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">年投入运营。新工厂主要生产中型轴承，以补充现有工厂的大型轴承系列产品。两大工厂的工业轴承将主要服务以下行业的客户：电机、工业电气、工业传动、冶金、矿山及水泥、工程机械、制浆及造纸、可再生能源。</span><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-size:small;font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;</span></span></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">总裁兼首席执行官汤母•强斯顿（</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">Tom Johnstone</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">）说道：“对</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">集团而言，中国是十分重要的市场，新工厂的开业深化了我们对中国及亚洲其他地区客户和经销商的承诺。大连两家工厂的投资总额约为</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">15</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">亿瑞典克朗（合约</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">15</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">亿人民币），将全面支持我们在工业市场的积极发展。我们将在该工厂采用最新技术，通过与在上海新建的‘斯凯孚全球技术中心’通力合作，缩短新产品开发周期，支持</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">在中国工业市场的长期技术发展。”</span><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-size:small;font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;</span></span></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">中国总裁艾澜德（</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">Erik Nelander</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">）说道：“在中国扩大</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">中型工业轴承的生产能够进一步巩固我们在工业轴承市场的地位。我们可以显著缩短交付周期，并且与重要客户进行近距离的互动。大连第一家工厂获奖众多，例如，连续两年获得大连市最佳雇主的殊荣，我们对它的发展状况感到十分满意。这也是</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">在大连建造第二家工厂的原因之一，我们坚信新工厂同样会取得良好的发展。”</span><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-size:small;font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;</span></span></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">大连位于大连经济和技术开发区，包括大型轴承工厂和中型轴承工厂，是</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">中国在工业产品销售中规模最大的轴承基地。截止至</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">2011</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">年年底，预计两家工厂将聘用</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">800</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">多名员工。</span><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-size:small;font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;</span></span></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">中国拥有众多销售和服务办事处以及</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">13</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">家生产单位，为亚洲区客户提供高品质的产品和服务。</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">1912</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">年，</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">首次在上海设立销售代理商，并于</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">1916</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">年，在上海成立首家销售分公司。如今</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">, SKF</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">中国的总部位于上海，拥有近</span><span lang=\"EN-US\"><span style=\"font-family:Calibri;\"><span style=\"font-size:14px;\">5000</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">名员工。</span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p></o:p></span>&nbsp;</p>', '斯凯孚,SKF', '斯凯孚,SKF', '10', '2011-10-31 08:56:11', '2011-10-31 22:45:22', null);
INSERT INTO `kp_information_column` VALUES ('65', '配件营销', '斯必克全球发布新一代节能环保汽车空调冷媒回收机', '立配网', '', '立配网', '已发布', '2011-10-31 09:16:38', '', '<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-size:10.5pt;\" lang=\"EN-US\"><span style=\"font-family:宋体;\">(</span></span><span style=\"font-size:10.5pt;\"><span style=\"font-family:宋体;\">美国北卡罗来纳州夏洛特，<span lang=\"EN-US\">2011</span>年<span lang=\"EN-US\">7</span>月<span lang=\"EN-US\">28</span>日<span lang=\"EN-US\">)</span>斯必克公司（<span lang=\"EN-US\">NYSE:SPW</span>）旗下汽车服务方案业务单元宣布，该公司用于汽车维修服务的新一代<span lang=\"EN-US\">ROBINAIR(</span>罗宾耐尔<span lang=\"EN-US\">)AC1234</span>系列汽车空调冷媒回收机今日在全球范围内同步上市。此外，公司同时宣布<span lang=\"EN-US\">ROBINAIR</span>（罗宾耐尔）的两个新系列产品<span lang=\"EN-US\">AC1234-7</span>和<span lang=\"EN-US\">AC1234-8</span>也在近期通过了德国汽车工业协会（<span lang=\"EN-US\">VDA</span>）的认可。德国汽车工业协会一向以对汽车产品的安全，品质以及可持续性提出最高技术要求而著称。</span><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><?xml:namespace prefix = v /><v:shapetype id=\"_x0000_t75\" o:preferrelative=\"t\" o:spt=\"75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" coordsize=\"21600,21600\"><span style=\"font-family:宋体;\"> </span></v:shapetype></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\" align=\"center\"><span style=\"font-size:10.5pt;\"><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><v:shapetype o:preferrelative=\"t\" o:spt=\"75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" coordsize=\"21600,21600\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031020348_33094.jpg\" width=\"350\" /></v:shapetype></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\">在欧洲，汽车制造商被要求为今年新引进的汽车款型配备包括<span lang=\"EN-US\">R-1234yf</span>在内的更加环保的空调冷媒，由此来大幅降低温室气体的排放量，而<span lang=\"EN-US\">2017</span>年以后制造的所有车辆则必须使用新型冷媒。斯必克的新产品<span lang=\"EN-US\">ROBINAIRAC1234</span>汽车空调冷媒回收机就是专门为<span lang=\"EN-US\">R-1234yf</span>空调冷媒的匹配使用而设计。斯必克计划在德国，美国和亚洲批量生产该回收机，并将其推广至全球市场。<span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><?xml:namespace prefix = o /><o:p></o:p></span></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\">“德国汽车工业协会在全球范围内获得汽车工业领域的广泛认可和尊敬。我们针对新型<span lang=\"EN-US\">R-1234yf</span>冷媒设计的回收系统能够获得他们的认可，对我们来说是一项非常重大的成就。”斯必克汽车服务方案的总裁谭瓦•阿尔菲（<span lang=\"EN-US\">Tanvir Arfi</span>）说，“此外，由于欧洲已经开始使用<span lang=\"EN-US\">R-1234yf</span>冷媒，我们很有信心，这项权威第三方认证能帮助我们的产品赢得欧洲汽车制造商们的支持与认可。”<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\">德国汽车工业协会委托全球知名的产品技术、安全及认证服务供应商<span lang=\"EN-US\">TUV</span>德国莱茵认证集团为<span lang=\"EN-US\">ROBINAIRAC1234</span>汽车空调冷媒回收机进行评估。获得德国汽车工业协会认可是许多欧洲汽车原厂的采购标准之一。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\" lang=\"EN-US\">ROBINAIR</span><span style=\"font-size:10.5pt;\">（罗宾耐尔）系列产品在设计上力求可以更为精确、安全地回收空调冷媒。<span lang=\"EN-US\">AC1234-8</span>型号的机器包含了内置式冷媒鉴别仪，而<span lang=\"EN-US\">AC1234-7</span>型号机器则支持外置式冷媒鉴别仪的使用。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\">“长期以来，斯必克一直致力于缓解汽车空调系统所造成的严峻的环境压力。而现在，更为绿色环保的<span lang=\"EN-US\">R-1234yf</span>空调冷媒的出现及其未来的大规模使用给我们的技术创新带来了挑战，这同时也是再一次展现我们创新实力的绝佳机会。”阿尔菲解释，“<span lang=\"EN-US\">ROBINAIR</span>（罗宾耐尔）品牌是在空调冷媒回收领域具有悠久历史的行业领导者，而斯必克也将再一次引领这一行业逐步向新型<span lang=\"EN-US\">R-1234yf</span>冷媒应用的转移，并对今后所有采用该项新型冷媒配置的车辆实施标准化服务流程。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\">空调冷媒中含有一些化学成分会释放出温室气体，从而造成环境变化。而近期研发的<span lang=\"EN-US\">R-1234yf</span>空调冷媒对可持续的环保型空调技术创新而言，具有里程碑的意义，其全球温室效应潜能值（<span lang=\"EN-US\">GWP</span>）只有<span lang=\"EN-US\">4GWP</span>，而其上一代冷媒则达到<span lang=\"EN-US\">1430GWP</span>。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\">早在<span lang=\"EN-US\">2007</span>年，美国环境保护署就曾为斯必克颁发奖项，表彰其旗下<span lang=\"EN-US\">ROBINAIR</span>（罗宾耐尔）品牌冷媒回收加注系统在减少冷媒排放进入大气方面的卓越功效及其所达到的严格环保标准。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\">斯必克汽车服务方案是全球领先的诊断系统以及服务解决方案的开发商和制造商，专门服务于汽车制造原厂，其经销商网络，零售和售后市场。</span></span></p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\">&nbsp;</p>\r\n<p style=\"line-height:21pt;text-indent:21pt;\"><span style=\"font-family:宋体;\"><span style=\"font-size:10.5pt;\"><span lang=\"EN-US\"><o:p></o:p></span></span></span>&nbsp;</p>', 'SPX', 'SPX', '11', '2011-10-31 09:04:17', '2011-11-08 09:57:54', null);
INSERT INTO `kp_information_column` VALUES ('66', '配件营销', '美国A123系统为斯密斯电动汽车供应电池', '立配网', '', '立配网', '已发布', '2011-10-31 09:16:46', '', '<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">美国锂离子电池供应商</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">A123</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">系统公司（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">A123Systems</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）日前表示，该公司已经和美国斯密斯电动汽车公司（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">SmithElectricVehicles</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）达成协议。根据协议，该公司将为斯密斯电动汽车公司的纯电动商用汽车供应电池模块。据悉，首批功率</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">5</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">千瓦／小时的车用电池模块将于</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">2011</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">年下半年开始装箱付运。</span><span style=\"font-family:\'Times New Roman\';\"> <span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><?xml:namespace prefix = o /><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><?xml:namespace prefix = v /><v:shapetype id=\"_x0000_t75\" o:preferrelative=\"t\" o:spt=\"75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" o:extrusionok=\"f\" gradientshapeok=\"t\"></v:path><o:lock v:ext=\"edit\" aspectratio=\"t\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031020514_77434.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">斯密斯电动汽车首席执行官布莱恩·韩赛尔（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">BryanHansel</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）表示：“我们选定了</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">A123</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">系统作为我们的锂离子电池战略供应商，因为该公司的产品能满足安全、高性能和使用寿命长的要求，并且价格具有竞争力。”</span><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-family:\'Times New Roman\';font-size:small;\">&nbsp;</span></o:p></span><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">A123</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">电池技术将首先应用于该公司生产的</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">SmithNewton</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">电动卡车上。这款载重在</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">7.5-15</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">吨的纯电动卡车将面向全球发售，其客户包括菲多利（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Frito-Lay</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）、世界最大的办公用品零售商</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Staples</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">、可口可乐、森宝利超市（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Sainsbury\'s</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）和乳业巨头</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">DairyCrest</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，以及美国海军陆战队</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">(U.S.MarineCorps)</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">。</span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p></o:p></span>&nbsp;</p>', 'A123,电动', 'A123', '11', '2011-10-31 09:05:42', '2011-11-08 09:57:38', null);
INSERT INTO `kp_information_column` VALUES ('67', '配件营销', '德尔福法兰克福车展推无线电动车充电技术', '立配网', '', '立配网', '已发布', '2011-10-31 09:16:41', '', '<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">德尔福在本月的法兰克福车展上展示了其高效率的无线电动车充电系统。</span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><?xml:namespace prefix = o /><o:p><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031020631_73188.jpg\" width=\"350\" /></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:\'Arial\',\'sans-serif\';mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><?xml:namespace prefix = v /><v:shapetype id=\"_x0000_t75\" o:preferrelative=\"t\" o:spt=\"75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" o:extrusionok=\"f\" gradientshapeok=\"t\"></v:path><o:lock v:ext=\"edit\" aspectratio=\"t\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">该电动车充电系统不需要充电电线，驾驶员只需要把他们的电动车停在车库里或者停车场就可以进行充电，这些车库和停车场地板上内置了无线的能量源，该能量源通过震荡磁场自动向电动车电池组传递电能。该无线充电技术不受任何自然环境（雨、雪、结冰）的影响，而且可以适用于不同尺寸和高度的车辆。</span><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-family:\'Times New Roman\';font-size:small;\">&nbsp;</span></o:p></span><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">德尔福混合动力汽车研发部总监</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Randy Sumner</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">表示：“我们已经验证了该技术的可行性和安全性，该技术符合电动车市场的各种要求，将被运用到新一代的电动车辆中。”</span></span><span lang=\"EN-US\"><o:p></o:p></span></p>', '德尔福,无线', '德尔福', '11', '2011-10-31 09:06:50', '2011-11-08 09:57:10', null);
INSERT INTO `kp_information_column` VALUES ('68', '配件营销', '博格华纳推出新型静音链传动产品', '立配网', '', '立配网', '已发布', '2011-10-31 09:17:01', '', '<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">博格华纳的优化静音链传动产品具有耐用性和低噪音的特性，适用于气油直喷式发动机的平衡轴传动装置。</span><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><?xml:namespace prefix = o /><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"color:#333333;mso-no-proof:yes;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><?xml:namespace prefix = v /><v:shapetype id=\"_x0000_t75\" o:preferrelative=\"t\" o:spt=\"75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" o:extrusionok=\"f\" gradientshapeok=\"t\"></v:path><o:lock v:ext=\"edit\" aspectratio=\"t\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031020800_50499.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">密歇根州奥本山，</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">2011</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">年</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">9</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">月</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">12</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">日</span><span style=\"font-family:\'Times New Roman\';\"> </span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">–</span><span style=\"font-family:\'Times New Roman\';\"> </span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">博格华纳（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">BorgWarner</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）最近推出其新型静音链传动产品，适用于现代汽车的</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Theta II</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">型四缸气油直喷式（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">GDI</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）和汽油涡轮增压直喷式（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">GDI</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）发动机的平衡轴传动装置。这些发动机为行销美国和韩国的现代索纳塔（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Hyundai Sonata</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）和起亚索兰托（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Kia Sorento</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）、</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">K5</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">及起亚狮跑（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Sportage</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）提供动力。新型静音链传动产品的设计旨在能够承受恶劣的环境条件和气油直喷式（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">GDI</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）发动机的压力，与适用于平衡传动装置的滚子链系统相比，其噪声、振动与平顺性（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">NVH</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）更低，耐用性更佳。</span><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-family:\'Times New Roman\';font-size:small;\">&nbsp;</span></o:p></span><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">“博格华纳（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">BorgWarner</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）的新型静音链传动技术使得气油直喷式（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">GDI</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）发动机的设计者采用一种简化的平衡轴驱动系统，与滚子链传动装置相比，其成本更低、噪声更低、耐久性更佳，”博格华纳摩斯链条（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">BorgWarner Morse TEC</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）总裁兼总经理詹姆斯</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">·</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">维利亚（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">James Verrier</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）表示。“博格华纳（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">BorgWarner</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）的工程师不断创新和研发新技术，提高发动机的效率和性能。我们很高兴与现代和起亚推出这款新型静音链传动产品。”</span><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-family:\'Times New Roman\';font-size:small;\">&nbsp;</span></o:p></span><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">直列式发动机缺乏</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">V6</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">和</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">V8</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">发动机中</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">V</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">形的内在稳定性，因此平衡轴传动装置的使用旨在帮助降低发动机的振动。这些传动装置的运行速度通常快于发动机正时系统，从而使耐久性成为平衡轴传动链的一个重要特性。博格华纳（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">BorgWarner</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）的新型静音链传动产品采用齿形链设计，旨在减少表面压强并提高耐磨性，从而能够承受汽油直喷式（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">GDI</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）发动机的恶劣环境条件和极端温度。</span><span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-family:\'Times New Roman\';font-size:small;\">&nbsp;</span></o:p></span><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">与滚子链传动装置相比，博格华纳（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">BorgWarner</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）推出的适用于平衡轴传动装置的静音链传动产品使平衡传动系统的成本更低，运转速度更高，</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"> NVH</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">更低，耐用性更佳。除了新型静音链传动产品，博格华纳（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">BorgWarner</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）还提供了平衡轴传动装置组件，包括张紧器、控制臂和导轨。</span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p></o:p></span>&nbsp;</p>', '博格华纳,传动', '博格华纳', '11', '2011-10-31 09:08:28', '2011-11-08 09:56:07', null);
INSERT INTO `kp_information_column` VALUES ('69', '配件营销', '汽车“亮眼” 欧司朗展出两款全新LED车头灯原型', '立配网', '', '立配网', '已发布', '2011-10-31 09:16:49', '', '<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">在</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">2011</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">年</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">9</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">月</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">26</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">至</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">28</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">日于德国达姆施塔特</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">(Darmstadt)</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">举行的国际汽车照明研讨会</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">(ISAL)</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">上，欧司朗光电半导体将展出两款全新</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">LED</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">车头灯原型：</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">OSLONBlackFlat</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">和</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">OSRAMOSTARHeadlampPro</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">。这两款原型均采用了全新的芯片和封装技术，并且都装配有陶瓷转换器。同时，它们也具备许多优点，包括即使在高电流下也可实现的高光输出、均匀的光型、热稳定性以及非常好的对比率。</span><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><?xml:namespace prefix = o /><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-no-proof:yes;mso-bidi-font-size:10.5pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><?xml:namespace prefix = v /><v:shapetype id=\"_x0000_t75\" o:preferrelative=\"t\" o:spt=\"75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" o:extrusionok=\"f\" gradientshapeok=\"t\"></v:path><o:lock v:ext=\"edit\" aspectratio=\"t\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-family:\'Times New Roman\';font-size:small;\"><span style=\"font-size:14px;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031021016_53586.jpg\" width=\"350\" />&nbsp;</span></span></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"mso-spacerun:yes;\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">作为汽车的“眼睛”，车头灯需要采用更具灵活性的光源。这些光源不仅要可靠、能在所有可视和驾驶条件下提供合适的光量，而且还必须执行各种功能，应对不断变化的环境条件，包括车头灯本身的高温。为了满足这些要求，欧司朗研发出了两款全新的</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">LED</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">原型：</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">OSLONBlackFlat</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">和</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">OSRAMOSTARHeadlampPro</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">。</span><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">&nbsp;</span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;mso-spacerun:yes;\">&nbsp;</span><span style=\"font-size:14px;\">OSLONBlackFlat</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">是</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">OSLON</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">黑色系列的最新成员，配备了陶瓷转换器和</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">QFN</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">QuadFlatNoLeads</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，方形扁平无引脚）封装。该款产品的典型热阻为</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">5K/W</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，比黑色系列其他成员的热阻低</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">20%</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">。由于特制材料的热膨胀系数与金属芯板的膨胀系数一致，所以黑色封装就意味着高稳定性。这款全新的</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">LED</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">不需要搭载透镜，因此它发出的光线可以非常靠近地注入导光板等。此外，其焊盘与整个产品系列中所使用的焊盘相同，所以即使是采用大致相同的线路板设计也可以大幅度地增加光输出。这款新产品的牵引功率为</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">2.3W</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，工作电流为</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">700mA</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，可实现</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">190lm</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">的典型光通量。</span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:14px;\">OSRAMOSTARHeadlampPro</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">则可满足输出和环境条件适应性方面的诸多要求，与其先前版本相比，可提供更为均匀的光型、更好的热稳定性和更高的亮度。这款</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">20x20mm</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">的高通量</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">LED</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">提供搭载</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">2</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">至</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">5</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">个芯片的多个版本。</span><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">&nbsp;</span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">&nbsp;&nbsp;&nbsp;&nbsp;如果采用全新</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">OSTAR</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">解决方案，车头灯制造商有望大幅降低成本，即更少的投入，却可实现更高的输出。只要搭载可单独或通过串联方式控制的芯片，便可实现</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">AFS</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">AdaptiveFrontlightingSystem</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，自适应前照灯系统）功能，从而引入允许采用矩阵芯片解决方案的配置。单个芯片的典型光通量约为</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">250lm</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">（工作电流为</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">1A</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">），依此可相应得出</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">5</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">芯片版本的光通量约为</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">1250lm</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">；</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">5</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">芯片版本的热阻为</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">2.1K</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，同样比先前型号的热阻低了</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><span style=\"font-size:14px;\">0.5K</span></span></span><span style=\"font-family:宋体;font-size:14px;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">。</span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:12.0pt;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';mso-bidi-font-family:\'Times New Roman\';mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">&nbsp;&nbsp;&nbsp;&nbsp;欧司朗光电半导体德国总部的汽车</span><span style=\"font-family:\'Times New Roman\',\'serif\';font-size:14px;mso-fareast-font-family:宋体;mso-bidi-font-size:12.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">LED</span><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:12.0pt;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';mso-bidi-font-family:\'Times New Roman\';mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">总监</span><span style=\"font-family:\'Times New Roman\',\'serif\';font-size:14px;mso-fareast-font-family:宋体;mso-bidi-font-size:12.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">PeterKnittl</span><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:12.0pt;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';mso-bidi-font-family:\'Times New Roman\';mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">对此充满信心：“这两款原型将全新的技术和全新的功能集于一身，完全满足车头灯系统的所有要求，因此对所有车型都极具吸引力，易于推广使用。”所有原型均基于</span><span style=\"font-family:\'Times New Roman\',\'serif\';font-size:14px;mso-fareast-font-family:宋体;mso-bidi-font-size:12.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">UX:3</span><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:12.0pt;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';mso-bidi-font-family:\'Times New Roman\';mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">芯片技术，在高电流下实现高光输出。它们配备的陶瓷转换器则负责确保光型的均匀性。借助先进的封装技术，将芯片直接封装在反射器中，使得光型中能够生成清晰的明</span><span style=\"font-family:\'Times New Roman\',\'serif\';font-size:14px;mso-fareast-font-family:宋体;mso-bidi-font-size:12.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">/</span><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:12.0pt;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';mso-bidi-font-family:\'Times New Roman\';mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">暗边界，从而实现非常好的对比率。所有这些特性都充分证明：这两款</span><span style=\"font-family:\'Times New Roman\',\'serif\';font-size:14px;mso-fareast-font-family:宋体;mso-bidi-font-size:12.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">LED</span><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:12.0pt;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';mso-bidi-font-family:\'Times New Roman\';mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">是高光效车头灯的理想光源。两款原型目前均已提供首批样品，计划于</span><span style=\"font-family:\'Times New Roman\',\'serif\';font-size:14px;mso-fareast-font-family:宋体;mso-bidi-font-size:12.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">2012</span><span style=\"font-family:宋体;font-size:14px;mso-bidi-font-size:12.0pt;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';mso-bidi-font-family:\'Times New Roman\';mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">年第三季度投放市场。</span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>', '欧司朗,车头灯,LED', '欧司朗', '11', '2011-10-31 09:10:41', '2011-11-08 09:55:43', null);
INSERT INTO `kp_information_column` VALUES ('70', '配件营销', '固特异轮胎独家供应Fisker Karma', '立配网', '', '立配网', '已发布', '2011-10-31 09:16:53', '', '<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">据美通社报道，</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Fisker</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">正在向市场推出首批</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">2012</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">年款混合动力跑车卡玛</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Karma</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，而该款跑车的轮胎由固特异独家供应。</span><span style=\"font-family:\'Times New Roman\';\"> <span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><?xml:namespace prefix = o /><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:\'ˎ̥\',\'serif\';color:#2b2b2b;mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><?xml:namespace prefix = v /><v:shapetype id=\"_x0000_t75\" o:preferrelative=\"t\" o:spt=\"75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" o:extrusionok=\"f\" gradientshapeok=\"t\"></v:path><o:lock v:ext=\"edit\" aspectratio=\"t\"></o:lock></v:shapetype></span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031021455_55721.jpg\" width=\"350\" /></span></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">2012</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">年款混合动力跑车</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">FiskerKarma</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">不仅具有纯电动车零排放的特点，而且具有传统轿车全速行驶的特性。为了给其提供</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">22</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">英寸的车轮和高扭力输出，固特异轮胎同菲斯克密切协作特地为其研发出了高性能轮胎，为其前后轴分别研发出</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">255/35/R22</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">和</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">285/35/R22</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">超级跑车轮胎</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">EagleF1</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">。</span><span style=\"font-family:\'Times New Roman\';\"> <span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">不止是</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">FiskerKarma</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">，丰田普锐斯、赛恩</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">iQ</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">以及雪佛兰沃蓝达等等很多汽车都采用固特异轮胎。一般来说，北美的新车型大多安装固特异轮胎而非其它品牌轮胎。</span></span><span lang=\"EN-US\"><o:p></o:p></span></p>', '固特异,轮胎', '固特异,轮胎', '11', '2011-10-31 09:15:29', '2011-11-08 09:55:20', null);
INSERT INTO `kp_information_column` VALUES ('71', '配件营销', '辉门为奔驰重卡阿克托斯研发刹车片', '立配网', '', '立配网', '已发布', '2011-10-31 09:16:58', '', '<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">辉门日前获得奔驰重卡阿克托斯（</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Actros</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">）的刹车片供应合同，根据该重卡前后轴刹车片的不同要求，辉门为其研发出了两种刹车片。辉门为阿克托斯的前轴研发了</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Knorr-BremseSN7-30</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">刹车片，为其后轴研发了</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">Knorr-BremseSN7-25</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">刹车片，这两种刹车片具有耐高温、经久耐用、高性能、防震颤以及提高驾驶员的舒适度等特点。</span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><?xml:namespace prefix = o /><o:p><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031021615_10663.jpg\" width=\"350\" /></o:p></span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><a href=\"http://www.360che.com/pic/16860_74678.html\" target=\"_blank\"><span style=\"color:#03329c;text-decoration:none;mso-no-proof:yes;mso-bidi-font-size:10.5pt;text-underline:none;\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><?xml:namespace prefix = v /><v:shapetype id=\"_x0000_t75\" o:preferrelative=\"t\" o:spt=\"75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" o:extrusionok=\"f\" gradientshapeok=\"t\"></v:path><o:lock v:ext=\"edit\" aspectratio=\"t\"></o:lock></v:shapetype></span></a><o:p></o:p></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">辉门刹车片产品营销总监</span><span lang=\"EN-US\"><span style=\"font-family:\'Times New Roman\';\">SilvanoVeglia</span></span><span style=\"font-family:宋体;mso-ascii-font-family:\'Times New Roman\';mso-hansi-font-family:\'Times New Roman\';\">表示：“我们同戴姆勒公司密切协作，根据其不同的要求研发出针对前轴和后轴的两种刹车片，使重卡阿克托斯取得了性能和耐用度的最佳平衡。”</span><span style=\"font-family:\'Times New Roman\';\"> </span></span></p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"text-indent:21.75pt;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:\'Times New Roman\';\"><span lang=\"EN-US\"><o:p></o:p></span></span></span>&nbsp;</p>', '辉门,刹车片', '辉门', '11', '2011-10-31 09:16:27', '2011-11-08 09:54:49', null);
INSERT INTO `kp_information_column` VALUES ('72', '汽配热点', 'LED车灯面临五大技术挑战', '立配网', '', '立配网', '已发布', '2011-10-31 10:14:45', 'thumb_4eae044ede291.jpg', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">国内车用<span lang=\"EN-US\">LED</span>的规模目前仅占汽车光源市场<span lang=\"EN-US\">10%</span>左右的小份额<span lang=\"EN-US\">,</span>目前主要还是比较集中在高端车应用中。尽管<span lang=\"EN-US\">LED</span>灯成本在持续下降，但要做到与传统光源不相上下，还需要在质量和产量上逐步提高，才能降低<span lang=\"EN-US\">LED</span>成本，扩大<span lang=\"EN-US\">LED</span>的汽车</span><span lang=\"EN-US\"><a href=\"http://www.ec.hc360.com/\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">电子</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">市场占有份额。<span lang=\"EN-US\">&nbsp; <!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"text-align:center;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031031304_57531.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　<b style=\"mso-bidi-font-weight:normal;\">五大技术挑战<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></b></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">　　成本高并非是延缓<span lang=\"EN-US\">LED</span>灯在汽车电子应用的唯一因素，一些技术性能方面的难题也是需要去解决的，比如单个<span lang=\"EN-US\">LED</span>的光照强度仍显不足，这就不能够随意减少<span lang=\"EN-US\">LED</span>的数目去缩小</span><span lang=\"EN-US\"><a href=\"http://www.lamp.hc360.com/\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">灯具</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">体积。<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　同时较小的体积不利于光源散热，这就需要<span lang=\"EN-US\">LED</span>厂家做好产品的热管理，才能将<span lang=\"EN-US\">LED</span>的优势发挥得更充分，无论对于<span lang=\"EN-US\">LED</span>的寿命或演色性都能提供较佳的状态，否则只能是适得其反，降低了产品质量。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">　　<span lang=\"EN-US\">LED</span>驱动系统的转换效率也是目前比较关键的问题点，在大功率应用中很多方案在兼顾成本的同时转换效率并不高<span lang=\"EN-US\">;</span>还有目前我国<span lang=\"EN-US\">LED</span>灯应用在汽车电子都属于高端车，而高端车都大部分在国外汽车厂商控制，国外厂家对<span lang=\"EN-US\">LED</span></span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/qyjj.shtml\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">供应商</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">的选择和认证非常苛刻，国内<span lang=\"EN-US\">LED</span>企业很难进入其供应链中。<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　再有我国的<span lang=\"EN-US\">LED</span>灯生产水平大部分还停留在民用产品的要求范围内，很难达到汽车级应用标准，即使达到汽车级应用的一些厂家可能生产能力及保证产品一致性等方面也不一定够，很难满足量产汽车的大批量高质量的需求。<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　因此<span lang=\"EN-US\">LED</span>灯要想加快在整个汽车光源上的占有率，必须首先在这些问题上加大解决力度，如上所述的热管理问题、生产成本及产品一致性问题等等。<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　<b style=\"mso-bidi-font-weight:normal;\">　汽车要求分立式<span lang=\"EN-US\">LED</span>驱动电源<span lang=\"EN-US\">&nbsp;<o:p></o:p></span></b></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　用恒压源驱动不能保证<span lang=\"EN-US\">LED</span>亮度的一致性，并且影响<span lang=\"EN-US\">LED</span>的可靠性、寿命和光衰。因此，<span lang=\"EN-US\">LED</span>必须使用恒流源驱动。大多数<span lang=\"EN-US\">LED</span>都有规定的电流值，可以使用配置为恒流源的线形稳压器来驱动，线性稳压器具有减少零部件数和降低电磁干扰<span lang=\"EN-US\">(EMI)</span>的优点，但在效率和热耗方面有严重的弊端，不适合大功率应用，用开关式电源<span lang=\"EN-US\">(SMPS)</span>则更适合在各种应用中。<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">　　目前采用<span lang=\"EN-US\">SMPS</span>模式<span lang=\"EN-US\">LED</span>驱动电源是市场主流，同时也分为分离器件组合方式和<span lang=\"EN-US\">IC</span>集成方式，<span lang=\"EN-US\">IC</span>集成模式如台湾聚积科技<span lang=\"EN-US\">(MacroBlock)</span>、<span lang=\"EN-US\">Zetex(Diodes)</span>、<span lang=\"EN-US\">TI</span>和<span lang=\"EN-US\">NSC</span>等，主要特点是内置<span lang=\"EN-US\">MOSFET</span>及其驱动电路，同时有些集成了多路驱动、自动调光和各种保护功能等，应用电路简单，体积小，易于控制成本，但是由于这种应用驱动电流小，设计不够灵活，控制精度不高，不能实现升压模式等原因，在多数汽车电子应用中有很大局限性，而采用<span lang=\"EN-US\">PWM</span>控制</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/zt/fdj/index.shtml\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">芯</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">片及外置<span lang=\"EN-US\">MOSFET</span>实现的分离模式则可以实现汽车电子中大功率，高效率以及高稳定性的应用，它可以通过不同的拓扑结构，结合<span lang=\"EN-US\">LED</span>对电压，电流和功率的需求，实现符合系统的整体目标。在成本上也会随着用量逐步降低。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21.1pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">欧洲前装市场主流方案<span lang=\"EN-US\">&nbsp;<o:p></o:p></span></span></span></b></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">　　目前<span lang=\"EN-US\">MCU+MCP1630</span>方案应用在欧洲应用相当广泛，占欧洲前装市场<span lang=\"EN-US\">70%</span>以上的市场，主要应用在大灯<span lang=\"EN-US\">(</span>远光灯<span lang=\"EN-US\">)</span>、</span><span lang=\"EN-US\"><a href=\"http://www.search.hc360.com/cgi-bin/seinterface.cgi?word=尾灯\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">尾灯</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">、闪灯等应用，该芯片以其高可靠性能，高效率，灵活的功能，高性价比在汽车电子<span lang=\"EN-US\">LED</span>驱动应用中树立了很好的口碑，为国外一些大厂商<span lang=\"EN-US\">LED</span>驱动的首选，同时，随着用量的不断增大，<span lang=\"EN-US\">MCP1630</span>成本优势也在越来越明显，完全可以在中低端车型中应用，更好地解决<span lang=\"EN-US\">LED</span>驱动中的热管理、效率、产品一致性等问题。<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　<span lang=\"EN-US\">MCP1630</span>是可用来开发智能电源的一种高速脉宽调制器。<span lang=\"EN-US\">MCP1630</span>在峰值电流模式下可以达到<span lang=\"EN-US\">1MHz</span>频率，<span lang=\"EN-US\">CMOS</span>输出驱动器可以直接控制<span lang=\"EN-US\">MOSFET</span>驱动器或者低端<span lang=\"EN-US\">N</span>沟道<span lang=\"EN-US\">MOSFET</span>，可提供外部参考电源，灵活实现输出电压及电流调节设置<span lang=\"EN-US\">;</span>内部高速比较器，高速带宽<span lang=\"EN-US\">/</span>误差放大器可以实现更快更精确的电流检测，同时集成了过温保护、欠压锁定功能，更好地实现更好的热管理。当与单片机配合，它可以通过控制电源系统的占空比以提供稳定的输出电压或电流。单片机则用来调整输出电压或电流、开关频率、最大占空比以及可提高电源系统智能化和可调节性的其他特性。<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">在<span lang=\"EN-US\">LED</span>驱动系统中，如果电源电压超出总<span lang=\"EN-US\">LED</span>电压，就需要降压转换器。如果<span lang=\"EN-US\">LED</span>组的电压超出电源电压，就需要升压转换器。在</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/xdc_cpcs.shtml\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">汽车电源</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">系统中，<span lang=\"EN-US\">LED</span>电压可能高于或低于电源电压，这样，就应该采用降压<span lang=\"EN-US\">/</span>升压或单端初级电感转换器<span lang=\"EN-US\">(SEPIC)</span>等电源拓扑技术。<span lang=\"EN-US\">MCP1630</span>能实现以上各种拓扑设计需要。同时秉承<span lang=\"EN-US\">Microchip</span>产品的一贯作风<span lang=\"EN-US\">(</span>高稳定性和产品一致性<span lang=\"EN-US\">)</span>，使<span lang=\"EN-US\">LED</span>的生产成本和质量都得到提高。下面就以<span lang=\"EN-US\">Microchip</span>公司的高速<span lang=\"EN-US\">PWM</span>芯片<span lang=\"EN-US\">MCP1630</span>为例，通过<span lang=\"EN-US\">SEPIC</span>拓扑结构去阐述该方案的在汽车电子应用中的适用性。<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　 <span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">　　除了可以作为<span lang=\"EN-US\">SEPIC</span>拓扑应用，<span lang=\"EN-US\">MCP1630</span>同样可以应用于单独的升压<span lang=\"EN-US\">/</span>降压电路中，主要应用于</span><span lang=\"EN-US\"><a href=\"http://www.auto.hc360.com/\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">汽车</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">头灯与日间行</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/pro-qcdj.shtml\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">车灯</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">等，可以实现<span lang=\"EN-US\">LED</span>串联，实现更高效控制应用<span lang=\"EN-US\">&nbsp; <o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">　　除了<span lang=\"EN-US\">MCP1630</span>应用，<span lang=\"EN-US\">Microchip</span>还推出了低成本<span lang=\"EN-US\">MCU</span>集成式<span lang=\"EN-US\">LED</span>驱动应用方案，如<span lang=\"EN-US\">PIC12HV615</span>，<span lang=\"EN-US\">PIC16HV785</span>，其内部集成了高压稳压器、高速硬件<span lang=\"EN-US\">PWM</span>模块、比较器、<span lang=\"EN-US\">ADC</span>、运放、电压基准等等，可以实现升压或者降压控制，应用于车载设备背光源、仪表背光源、刹车照明灯等小功率应用中，将传统<span lang=\"EN-US\">CCFL</span>背光转成<span lang=\"EN-US\">LED</span>背光。<span lang=\"EN-US\">MCU</span>的丰富资源可以实现更多的智能化功能，高度集成的应用降低了成本，也简化了设计，节约了空间，同样有助于克服热管理，提高效率。但是该应用目前还不太适合在大功率应用中。 </span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><o:p></o:p></span></span></span>&nbsp;</p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span lang=\"EN-US\"><o:p><span style=\"font-family:Calibri;font-size:small;\">&nbsp;</span></o:p></span></p>', 'LED,车灯', 'LED,车灯', '25', '2011-10-31 10:14:08', '2011-10-31 22:36:04', null);
INSERT INTO `kp_information_column` VALUES ('73', '汽配热点', '变速器技术遭外资垄断封锁待突破', '立配网', '', '立配网', '已发布', '2011-11-15 15:28:32', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">9</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">月<span lang=\"EN-US\">2</span>日<span lang=\"EN-US\">-4</span>日，由中国汽车技术研究中心、中国汽车工程学会、中国汽车工业协会、中国汽车报社和天津经济技术开发区管委会联合主办的<span lang=\"EN-US\">“2011</span>中国</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/zt/07lhyz/index.shtml\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">汽车产业</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">发展（泰达）国际论坛<span lang=\"EN-US\">”</span>（下称天津论坛）在天津经济技术开发区万丽泰达国际</span><span lang=\"EN-US\"><a href=\"http://www.hotel.hc360.com/\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">酒店</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">举行。与会的<span lang=\"EN-US\">100</span>余位政府领导和企业领袖、<span lang=\"EN-US\">600</span>余位国内外业界嘉宾围绕<span lang=\"EN-US\">“</span>汽车社会与产业未来<span lang=\"EN-US\">”</span>这一主题，就中国汽车工业的未来走向进行了精彩的交流和研讨，为汽车社会的健康发展建言献策。 <span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-size:small;\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031031611_14114.jpg\" width=\"350\" /></span></b></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">&nbsp;&nbsp;&nbsp;&nbsp;自动</span></b><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/zt/bsq091009/index.shtml\" target=\"_blank\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">变速器</span></span></span></b></a></span><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">技术堪称核心零部件中的核心技术。<span lang=\"EN-US\"><o:p></o:p></span></span></b></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">&nbsp;&nbsp;&nbsp;&nbsp;能否掌握诸如变速器等核心</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/tech.shtml\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">零部件技术</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">，一直是国内相关汽车企业中方老大们久治不愈的心病。那么这<span lang=\"EN-US\">“</span>心病<span lang=\"EN-US\">”</span>，该怎么治愈？关乎国内变速器技术发展的情况又如何呢？自主品牌的企业负责</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/zt/chukou/index.shtml\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">配套</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">的老总到天津某一个自动变速器的企业去，跟他们商讨配套的事。到了企业的门口，这家公司居然不让我们这个负责配套的老总厂家进去，根本不跟你谈。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">他们基本上在国内只生产不合资，如果生产也是以独资的方式进行。（比如说）通用现在也在做别的新产品（自动变速器），据我所知，他们开发好了以后，也会拿到烟台来。但烟台的变速器厂根本不跟上海合资，它是独资的。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">而针对自动变速器的市场层面，徐向阳说，<span lang=\"EN-US\">“</span>我们国家从<span lang=\"EN-US\">01</span>年开始到<span lang=\"EN-US\">2010</span>年，中国自动变速器平均年增长速度为<span lang=\"EN-US\">35%</span>。我们自动变速器的市场增长率要高于汽车行业的市场增长率。在很长一段时间内这种趋势会保持下去。一方面汽车市场在增长，同时，自动变速器装车的比例在提高，是两个增长率叠加在一起。它的增长速度比汽车行业的增长速度要快。目前国内的自动变速器配套率为<span lang=\"EN-US\">39%</span>，这<span lang=\"EN-US\">39%</span>主要是指的乘用车。如果考虑到</span><span lang=\"EN-US\"><a href=\"http://info.qipei.hc360.com/list/syc_cpcs.shtml\" target=\"_blank\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"color:#000000;\">商用车</span></span></span></a></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">，这个比例一下就会下来。因为我们商用车的自动变速器装车率还是很低。所以，我们国内自动变速器作为第一大的汽车产销国，但是比较悲哀的一点，我们的自动变速器的产业完全是由国外公司来控制的，尽管市场巨大。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">徐提出中方可在五个方面进行尝试突破。他说，<span lang=\"EN-US\">“</span>第一，我们要承认我们存在的技术差距，我们要积极的采取国际化合作的战略。通过国际化的合作来引进国外先进的技术，消化吸收掌握先进的核心技术。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">第二，我们要在国际化的合作中自主创新，培养自己的人才队伍。现在自动变速器行业非常缺人才。要是谁是学自动变速器的，掌握自动变速器的研发技术，各个公司都抢。很难找到这样的人才，所以我们要培养这样的人才队伍。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;“</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">第三，我们要形成自己的产业链。包括从原材料一直到整车，形成一个产业链。只有产业链强大了，自动变速器行业才能强大。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;“</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">第四，自动变速器企业自身我们要合作共赢，大家能够共享产业链。润滑油，如果长城润滑油好的话，大家都选它，你培养出一家能够为这个行业服务的企业。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;“</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">第五，整车企业要敢于吃螃蟹，去选用自主品牌的自动变速器，来把这个产业配置。如果自主品牌的整车企业不选用自主品牌的自动变速器的话，我们造出来给谁用？跨国公司一开始不会用的。只有在整车企业上用了，大家验证了你的可靠性、你的质量，他才能够推广到跨国公司去，才能使我们的产品更有竞争力。<span lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">徐还总结说，按照我国自动变速器产业化的进度，我们可以得出这样一个结论，<span lang=\"EN-US\">2012</span>年应该是中国自动变速器产业最关键性的一年，我们可以说是自动变速器走向产业化的元年。因为在<span lang=\"EN-US\">2012</span>年，各种类型的自动变速器都会由自主品牌的企业实现批量生产。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">&nbsp;&nbsp;&nbsp;&nbsp;2013</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">年，就会进入一个快速膨胀发展期。到了<span lang=\"EN-US\">2015</span>年国内自动变速器产业就会形成一个相当的规模。</span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span lang=\"EN-US\"><o:p></o:p></span></span></span>&nbsp;</p>', '变速器', '变速器', '20', '2011-10-31 10:16:49', '2011-11-15 15:28:32', '2011-11-15 15:28:32');
INSERT INTO `kp_information_column` VALUES ('74', '汽配热点', '中国锂电池发展背后藏隐患：专利之殇', '立配网', '', '立配网', '已发布', '2011-10-31 10:48:12', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">源自古希腊传说：迪奥尼修斯国王请他的大臣达摩克利斯赴宴，命其坐在用一根马鬃悬挂的一把寒光闪闪的利剑下。而如今的中国电动车发展也遭遇到了专利领域<span lang=\"EN-US\">“</span>达摩克利斯之剑<span lang=\"EN-US\">”<!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">近日，某企业代表指出，国内锂电池蓬勃发展的背后存在着专利隐患，由于不掌握专利主导权，国内<span lang=\"EN-US\">70%</span>新能源整车企业及电池供应商将来有可能会成为<span lang=\"EN-US\">“</span>被告<span lang=\"EN-US\">”</span>。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031031803_98748.jpg\" width=\"350\" /></span></span></b></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">市场呈现<span lang=\"EN-US\">“</span>繁荣<span lang=\"EN-US\">”<o:p></o:p></span></span></span></b></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">自<span lang=\"EN-US\">2009</span>年以来，随着国家对新能源汽车发展的投入力度不断加大，各项利好政策地不断出台，中国也一跃成为世界第一新能源汽车市场。同时，新能源汽车产业链的上游<span lang=\"EN-US\">——</span>锂电池制造企业，亦如<span lang=\"EN-US\">“</span>雨后春笋<span lang=\"EN-US\">”</span>般不断见诸各大展会及论坛。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">常熟合众环保能源科技研究所所长沙永康对记者表示，以一家年产<span lang=\"EN-US\">200</span>吨磷酸铁锂正极材料的小厂为例，其生产设备投资只需<span lang=\"EN-US\">220</span>万元（不包括检测设备仪器），而其一年的产值却高达<span lang=\"EN-US\">3000</span>万元。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">国联证券的一份研究报告表明，以磷酸铁锂为正极材料的锂离子电池，其毛利率高达<span lang=\"EN-US\">40%-70%</span>，远远超过作为正极材料的锰酸锂和钴酸锂（毛利率分别约为<span lang=\"EN-US\">40%</span>、<span lang=\"EN-US\">20%</span>）。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">记者采访了解到，以磷酸铁锂为电池正极的锂离子动力电池目前已经成为国内电动车产业中重要地位。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">申银万国的一份动力电池行业研究报告显示：目前国内已经公布的<span lang=\"EN-US\">17</span>款混合动力和纯电动车型中，有<span lang=\"EN-US\">11</span>款搭载了锂离子电池，而其中磷酸铁锂电池又占了绝大多数。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">中投顾问汽车行业分析师李胜茂预测，到<span lang=\"EN-US\">2012</span>年，新能源车的年产量将达到<span lang=\"EN-US\">100</span>万辆，按每辆新能源汽车电池成本七万元，动力锂电池的正极磷酸铁锂材料<span lang=\"EN-US\">52</span>公斤，负极材料<span lang=\"EN-US\">41</span>公斤，电解液<span lang=\"EN-US\">40</span>公斤计算，<span lang=\"EN-US\">100</span>万辆混合动力汽车将带动<span lang=\"EN-US\">5.2</span>万吨正极材料，<span lang=\"EN-US\">4.1</span>万吨负极材料，四万吨电解液的需求。对于国内电池厂商而言，这将是一个总产值<span lang=\"EN-US\">700</span>亿元的大蛋糕。而如果按客车计算，这一数值还将提高三倍。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">受益新能源汽车的快速发展，未来动力锂离子电池市场规模将高速增长。预计<span lang=\"EN-US\">2010</span>至<span lang=\"EN-US\">2018</span>年全球锂离子电池市场规模年均复合增长率高达<span lang=\"EN-US\">54.2%</span>，并于<span lang=\"EN-US\">2018</span>年达到<span lang=\"EN-US\">160</span>亿美元<span lang=\"EN-US\">.”</span>中投信德在《<span lang=\"EN-US\">2011-2015 </span>年中国锂电池铝塑膜市场调研及投资分析预测报告》中也同样指出。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">中国科学院物理研究所研究员黄学杰在接受记者采访时表示，目前国内做锂离子电池的企业达到<span lang=\"EN-US\">300</span>多家，而做磷酸铁锂材料的企业也达到<span lang=\"EN-US\">100</span>多家。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">繁荣背后之殇<span lang=\"EN-US\"><o:p></o:p></span></span></span></b></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">日本、中国和韩国是世界<span lang=\"EN-US\">3</span>大锂离子电池生产国，<span lang=\"EN-US\">2010</span>年全球锂离子电池的产量约为<span lang=\"EN-US\">50</span>亿只，中国生产了<span lang=\"EN-US\">16</span>亿只，占到了<span lang=\"EN-US\">32%</span>。<span lang=\"EN-US\">”</span>中国化学与物理电源行业协会秘书长刘彦龙日前表示。然而，如此庞大的锂电池产业，却面临极大的材料专利纠纷，这也成为悬在中国锂电池行业头顶的利剑。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">台湾立凯电能科技股份有限公司（以下简称<span lang=\"EN-US\">“</span>台湾立凯<span lang=\"EN-US\">”</span>）中国区总经理杨智伟告诉记者，在磷酸铁锂电池和材料领域，有三大核心技术专利是无法绕行的，其中一个是包敷碳技术专利，后两个是磷酸铁锂本身材料专利和碳热还原技术专利。前者已经在华申请，加拿大<span lang=\"EN-US\">Phostech</span>公司拥有独家使用权；后者尚未到中国申请专利，分别归属于加拿大<span lang=\"EN-US\">Phostech</span>公司和美国威能公司。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">浙江中宇锂电能源股份有限公司市场部经理郭华表示，国产隔离膜相比国外优秀隔离膜的主要区别在国产的均一性差，使用国产隔离膜会导致电池质量不稳定。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">在磷酸铁锂电池和材料上，中国绝对称不上先进。有少数企业称专利没有问题，那是瞎说，这里面有很多复杂的问题有待解决。随着加拿大<span lang=\"EN-US\">Phostech</span>公司在华获得包敷碳技术专利，以及威能等公司抢占中国市场步伐的加快，专利问题已经很现实地摆在了我们面前，不容回避。<span lang=\"EN-US\">”</span>郭华坦言。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">杨智伟告诉记者，以中国电池工业协会为代表，目前正在联合多家电池企业积极行动，收集证据，以证明加拿大<span lang=\"EN-US\">Phostech</span>公司去年在华申请的磷酸铁锂包敷碳技术专利无效。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">杨智伟补充道，目前，国内很多企业在磷酸铁锂本身材料及碳热还原技术方面已经取得很大进展，与国外水平接近。但是，在量产的一致性上，仍有差距。另外，在对包敷碳技术专利申诉方面，由于加拿大<span lang=\"EN-US\">Phostech</span>公司在<span lang=\"EN-US\">2000</span>年就已经获得专利，而国内是<span lang=\"EN-US\">2003</span>年以后才开始研究，这方面很难获得成功。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">记者从《专利法》公开的第<span lang=\"EN-US\">22</span>条规定了解到，专利申请至少要具备新颖性、创造性和实用性，而且没有其他实质性问题，才能被授权。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">国家知识产权局一位工作人员表示，最好的举证方法是找出一篇或多篇此前已经公开的文献，与专利的权利要求进行对比，如果重合，则可证明专利缺乏新颖性。而据一位企业人士透露，目前他们已经找到一篇公开文献，但是论据不是特别充分。一旦提起诉讼，胜算并不大。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">诉讼风险堪忧<span lang=\"EN-US\"><o:p></o:p></span></span></span></b></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">河南环宇董事长李中东表示，虽然磷酸铁锂本身材料专利与美国威能公司的碳热还原技术专利在中国没有获得申请，但是，两者却和包敷碳技术专利一起在美、日、德、英、法、意、加七国申请了专利保护。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">申银万国的分析师担心，未来，中国生产的磷酸铁锂电池一旦出口美、英等上述<span lang=\"EN-US\">7</span>国，将会遭遇专利风险。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">杨智伟向记者表示，我们公司统计过，目前国内企业所拥有的<span lang=\"EN-US\">220</span>个左右专利，都不是核心专利，很多专利申请都是用来报项目用的。目前获得加拿大<span lang=\"EN-US\">Phostech</span>公司专利授权在华开展业务的只有台湾立凯一家，而与其合作的国内企业只有<span lang=\"EN-US\">ATL</span>、力神、比克等少数几家，大多数电池企业均为侵权生产和销售。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">加拿大<span lang=\"EN-US\">Phostech</span>公司专利的持有人是否会对于整车厂下手<span lang=\"EN-US\">?</span>在这个专利的风险的背后，隐藏着更大的风险。杨智伟表示，翻开过往<span lang=\"EN-US\">phostech</span>诉讼的纪录，最早与<span lang=\"EN-US\">A123</span>的诉讼当中，在诉讼对象中虽然放了<span lang=\"EN-US\">B&amp;D</span>一马，包括给其做电池代工的比克，但是最终的结果却是比克放弃了<span lang=\"EN-US\">A123</span>代工，且<span lang=\"EN-US\">B&amp;D </span>停止对<span lang=\"EN-US\">A123</span>电池的采购。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">某业内人士表示担忧， 这样的模式是否真有可能在中国上演<span lang=\"EN-US\">?</span>而一旦发生该如何应对？国内<span lang=\"EN-US\">70%</span>新能源整车企业及电池供应商将来有可能会成为<span lang=\"EN-US\">“</span>被告<span lang=\"EN-US\">”</span>，是整个产业需要思考的问题。目前国内做纯电动汽车的整车厂如同当年的<span lang=\"EN-US\">B&amp;D</span>，而国内大大小小的动力电池供货商如同比克。随着中国新能源汽车产业的发展壮大，加拿大<span lang=\"EN-US\">Phostech</span>公司有可能挟此专利在中国形成一轮<span lang=\"EN-US\">“</span>诉讼潮<span lang=\"EN-US\">”</span>，中国想凭借着电动车产业走出去的梦想也有可能成为<span lang=\"EN-US\">“</span>幻想<span lang=\"EN-US\">”</span>；在国外所受到的诉讼也将会更为激烈。</span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><o:p></o:p></span></span></span>&nbsp;</p>', '锂电池', '锂电池', '20', '2011-10-31 10:19:17', '2011-10-31 22:35:35', null);
INSERT INTO `kp_information_column` VALUES ('75', '汽配热点', '汽车安全电子“屏争”破题', '立配网', '', '立配网', '已发布', '2011-10-31 10:48:06', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">早在今年<span lang=\"EN-US\">3</span>月举办的<span lang=\"EN-US\">2011CNF-</span>中国车联网产业发展论坛上，一众专家学者及汽车电子行业精英就曾提出<span lang=\"EN-US\">“</span>屏幕之争<span lang=\"EN-US\">”</span>的观点。这种观点认为，汽车电子行业未来的竞争将集中在车机那块小小的屏幕之上，未来所有车载电子产品和各项终端应用都必将通过一块屏幕显示，因此，谁占据了屏幕，谁就占据了主动权。<span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">基于这种心理，今年不少车机厂家纷纷着手扩充产品卖点，往自身掌握的这块屏幕里塞进更多功能和应用程序，比如微博应用，又或电子狗等安全电子产品模块。人人都想包办整个屏幕可以容纳的内容，然而现状是，没有一个车机厂商能够<span lang=\"EN-US\">“</span>一屏独大<span lang=\"EN-US\">”</span>，增加了更多应用功能程序，无疑考验厂家的研发能力以及产品稳定性，此外新增功能模块所产生的生产成本又该如何消化，又成为了众多厂家的头疼难题。面对这块小小的屏幕，是否真的有必要<span lang=\"EN-US\">“</span>为屏消得人憔悴<span lang=\"EN-US\">”</span>呢？<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031032032_57578.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">事实上，消费者并不在乎用谁家的屏，他们更在乎的是屏内装载的功能产品是否满足其真正需求，而关于屏的数量，当然谁也不乐见自己的爱车上摆放、吸附着各种形状大小的屏幕，破坏车内美感。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">那么，汽车电子厂商们何不放手<span lang=\"EN-US\">“</span>屏幕之争<span lang=\"EN-US\">”</span>，欲擒故纵，从落实产品便捷服务和市场销售入手。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">以汽车安全电子为例。汽车安全电子产品中诸如泊车前后视、胎压监测、行车记录仪、防侧撞系统等产品都已独立成产品品类，这些独立品类大多拥有独自的显示屏幕用于输出捕获的画面，由此增加了车内屏幕数量，然而相较之下，由于车机的先入为主，车机屏幕在车内的存在意义被消费者认为理所当然，而汽车安全电子产品的屏幕倒显得有些<span lang=\"EN-US\">“</span>编制之外<span lang=\"EN-US\">”</span>意味。因此，考虑到屏幕数量问题，车主只会挑选安全电子系列中的部分更需要的产品，导致汽车安全电子产品的各个品类市场不能得到均衡发展。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">在第五届中国广州国际汽车后市场博览会上，广东丰诺汽车安全科技有限公司首次亮相了其最新产品<span lang=\"EN-US\">--CS-BOX</span>安全辅驾中央处理系统。这项产品其中一大亮点是，其将目前所有汽车安全电子产品的单个功能整合在同一模块内，但各个功能的输出设备却并不限制，<span lang=\"EN-US\">“</span>只要有<span lang=\"EN-US\">AV-IN</span>借口的屏幕都可以输出<span lang=\"EN-US\">”</span>。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">CS-BOX</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">，从字面上是<span lang=\"EN-US\">“CarSafety-Box”</span>，即<span lang=\"EN-US\">“</span>汽车安全盒子<span lang=\"EN-US\">”</span>，产品硬件由一个盒状中央处理器，以及诸如胎压监测、泊车前后视、行车记录仪、电子狗等市场主流汽车安全电子产品组成。丰诺总经理孟海飞女士告诉慧聪网记者，通过中央处理器转接处理，无论是车机还是<span lang=\"EN-US\">PND</span>（即便二者中没有相关安全电子模块），都可以轻松输出数据分析画面，同时<span lang=\"EN-US\">“</span>不需要主机做任何改动<span lang=\"EN-US\">”</span>。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">丰诺此举即证明，撇开屏幕，汽车安全电子产品也能另创出路。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">事实上，汽车安全电子市场发展一直受到三大因素限制。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">首先是，单品竞争力不强。目前市场上流行的汽车安全电子产品种类繁多，但却都没有形成一个系列化的销售。<span lang=\"EN-US\">“</span>比方说摄像头，它不能单独构成一个产业链，太势单力薄，往往沦为被车主视为<span lang=\"EN-US\">\'</span>可有可无<span lang=\"EN-US\">\'</span>的配送品。<span lang=\"EN-US\">”</span>孟海飞告诉记者，产品的零散形式不仅不利于经销商对产品的系列营销，还不利于消费者的一次性消费。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">其次，系统兼容瓶颈。市场上销售各厂商的汽车安全电子产品大多只能满足一种安全需求，且不同厂商的产品，存在安装复杂、连接匹配不良、数据紊乱失真，无法满足车主日益增加的多种安全渴望的现象。关于这点，丰诺通过一个中央处理器将各种系统化零为整，整合成一个系统的做法俨然值得借鉴。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">再次，系列安装难。如前所述，假如车主希望安装一系列的汽车安全电子产品，那么，除了面对一堆屏幕烦恼外，还会令爱车被加上很多接线，另外拆线接线等繁多的安装手续，也极大地考验着车主等候的耐心。<span lang=\"EN-US\">“</span>如果逐个安装这些汽车电子产品，至少需要<span lang=\"EN-US\">8</span>个小时。<span lang=\"EN-US\">”</span>孟海飞说，而<span lang=\"EN-US\">CS-BOX</span>的安装不到<span lang=\"EN-US\">3.5</span>个小时。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">由此可见，屏幕之外，汽车安全电子产品还需着力解决产品一次性消费、系统兼容、安装繁琐方面的难题，破题关键在于：整合与开放。整合即，将车主对安全的全方面需求与前视、后视、胎压监测、行车记录、雷达测速等功能有机结合起来，并通过高性能模块的集成处理，实现完美匹配，使得各种系统性安全参数精确传到终端显示；开放即，放手终端屏幕，让各个厂家生产的产品都能够实现接口匹配，不排斥消费者对屏幕的个性选择。整合与开放兼得，才能为汽车安全电子市场带来更多差异化突破。</span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\">&nbsp;</p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span lang=\"EN-US\"><o:p></o:p></span></span></span>&nbsp;</p>', '电子产品', '电子产品', '20', '2011-10-31 10:21:51', '2011-10-31 22:35:44', null);
INSERT INTO `kp_information_column` VALUES ('76', '汽配热点', '网络收音机将在汽车市场盛行', '立配网', '', '立配网', '已发布', '2011-10-31 10:48:02', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">据<span lang=\"EN-US\">IHS iSuppli</span>公司的汽车市场研究报告，未来八年全球装备网络收音机的汽车销量将增长到<span lang=\"EN-US\">30</span>倍以上，带动一系列车载应用今后几年内将集成到汽车电子系统之中。<span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">2018</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">年，音响本体上集成网络收音机的汽车销量将从<span lang=\"EN-US\">2010</span>年的<span lang=\"EN-US\">16.8</span>万辆增长到<span lang=\"EN-US\">2400</span>万辆，如图<span lang=\"EN-US\">5</span>所示。美国将引领汽车网络收音机市场的发展，<span lang=\"EN-US\">2018</span>年该国销量将从<span lang=\"EN-US\">2010</span>年的<span lang=\"EN-US\">14.9</span>万增长到<span lang=\"EN-US\">1090</span>万。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-align:center;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031032235_77856.jpg\" width=\"350\" /></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">采用可以集成云内容的音响本体的汽车出货量大增，未来几年将刺激车载应用急剧增长。这些应用，不论是内置在汽车之中，还是通过智能手机等联网移动设备提供的，将提供多种信息、娱乐、远程诊断和导航服务。音乐作为车内主要娱乐形式已超过<span lang=\"EN-US\">75</span>年，预计网络收音机将引领车内应用革命。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">音响本体向基于应用的设计方向发展<span lang=\"EN-US\"><o:p></o:p></span></span></span></b></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">汽车电子系统的设计方向发生根本性改变，正在促进车内网络收音机的快速发展。汽车信息娱乐系统正在从固定功能音响本体向<span lang=\"EN-US\">IHS</span>公司所说的基于应用的音响本体转变。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">类似于<span lang=\"EN-US\">PC</span>，基于应用的音响本体将基于软件和硬件平台，该平台将通过应用提供所有功能。这些应用将运行于音响本体平台之上，并将提供基于人机接口<span lang=\"EN-US\">(HMI)</span>的用户界面。车厂已在车内提供<span lang=\"EN-US\">HMI</span>。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">音响本体中的智能手机集成软件允许驾车者利用众多汽车相关智能手机应用。智能手机也提供数据计划，包括接收内容与服务的无线通信成本。目前只有少数音响本体具备部分上述功能，但未来几年这种趋势将日益增强。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">网络收音机将是基于应用的音响本体中最常用应用之一。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">网络收音机上路<span lang=\"EN-US\"><o:p></o:p></span></span></span></b></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">美国约有<span lang=\"EN-US\">50</span>款车已经集成了网络收音机应用，或者将在<span lang=\"EN-US\">2012</span>年版中采用该应用。宝马、福特汽车、林肯、奔驰、<span lang=\"EN-US\">Scion</span>、别克、雪佛兰和现代在美国市场都集成了网络收音机应用。在欧洲，目前只有宝马和<span lang=\"EN-US\">Mini</span>提供网络收音机。在中国，有四款豪华车型正在添加网络收音机集成。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">网络收音机服务采用无线方式<span lang=\"EN-US\"><o:p></o:p></span></span></span></b></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">多家网络收音机服务提供商已经进入美国市场，包括<span lang=\"EN-US\">Pandora</span>、<span lang=\"EN-US\">iHeart-Radio</span>、<span lang=\"EN-US\">Slacker</span>和<span lang=\"EN-US\">Spotify</span>。这些公司的服务对标准的无线电广播及基于订阅的卫星广播构成直接竞争。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">在多数<span lang=\"EN-US\">OEM</span>和配件市场音响本体供应商中，<span lang=\"EN-US\">Pandora</span>事实上是基于云的网络收音机应用。例如，先锋提供了一种免费<span lang=\"EN-US\">PandoraLink</span>应用，需要一个适当的配件市场信息娱乐音响本体，以支持该音响本体与手机之间的无线连接，从而实现<span lang=\"EN-US\">Pandora</span>收音机的集成。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">Pandora</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">通过多种分销渠道来提供服务。除了向<span lang=\"EN-US\">PC</span>提供服务，<span lang=\"EN-US\">Pandora</span>还为智能手机开发了多种应用，并与<span lang=\"EN-US\">200</span>多家消费电子产品生产商建立了合作关系，包括<span lang=\"EN-US\">Alpine</span>、<span lang=\"EN-US\">Panasonic</span>、先锋、三星和索尼。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\">Pandora</span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\">还与主要汽车厂商发展关系，包括福特汽车、奔驰和<span lang=\"EN-US\">Mini</span>，并与主要汽车厂商的供应商建立联系，以把其服务集成到现有及未来的汽车音响系统之中。此外，通用汽车、现代与丰田均宣布了未来集成<span lang=\"EN-US\">Pandora</span>服务的计划。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">走进云世界<span lang=\"EN-US\"><o:p></o:p></span></span></span></b></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\">除了网络收音机，还有许多其它基于云的内容源。云音乐柜服务，比如苹果<span lang=\"EN-US\">iCloud</span>、<span lang=\"EN-US\">Google Music</span>或亚马逊<span lang=\"EN-US\">Cloud Drive</span>，由于这些应用在移动产业有流行潜力，所以可能用于汽车之中。但是，目前汽车厂商对把这类云服务引入汽车兴趣不大。相反，目前他们比较青睐<span lang=\"EN-US\">Pandora</span>、<span lang=\"EN-US\">iHeartRadio</span>和<span lang=\"EN-US\">Slacker</span>等流行的娱乐应用。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-family:宋体;font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\" lang=\"EN-US\">注：Egil Juliussen</span><span style=\"font-family:宋体;font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">是<span lang=\"EN-US\">IHS</span>公司汽车信息娱乐首席分析师。</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '网络收音机', '网络收音机', '20', '2011-10-31 10:23:08', '2011-10-31 22:35:53', null);
INSERT INTO `kp_information_column` VALUES ('77', '汽配热点', '润滑与汽车技术协调发展需“润滑”', '立配网', '', '立配网', '已发布', '2011-10-31 10:48:00', '', '<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">近日，中国石化汽车行业技术合作中心举办</span><span style=\"font-size:14px;\" lang=\"EN-US\">“</span><span style=\"font-size:14px;\">车辆传动系统暨车用油技术发展趋势</span><span style=\"font-size:14px;\" lang=\"EN-US\">”</span><span style=\"font-size:14px;\">研讨会，与来自国内外</span><span style=\"font-size:14px;\" lang=\"EN-US\">40</span><span style=\"font-size:14px;\">多家汽车企业的代表共同探讨汽车传动系统及相关车用油技术的发展趋势。</span><span lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">据了解，中国石化汽车行业技术合作中心成立于</span><span style=\"font-size:14px;\" lang=\"EN-US\">2009</span><span style=\"font-size:14px;\">年，是中国石化与汽车产业协同发展和技术进步的重要平台，已经成为石化行业与汽车行业深度合作的窗口。中国石化润滑油公司副总经理赵江表示：</span><span style=\"font-size:14px;\" lang=\"EN-US\">“</span><span style=\"font-size:14px;\">石化行业在燃油、车用化工材料、润滑油脂等方面与汽车产业有着紧密的联系，中国石化十分关注中国汽车产业的发展，与众多汽车企业和行业协会组织建立了密切的合作关系，通过面向汽车行业的服务平台中国石化汽车行业技术合作中心，努力推进汽车相关石化产品，尤其是润滑油的技术发展。</span><span style=\"font-size:14px;\" lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype> <p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p><img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031032746_29040.jpg\" width=\"350\" /></o:p></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\" lang=\"EN-US\"><o:p></o:p></span><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">据了解，汽车技术的发展需要与之匹配的润滑油技术，尤其是当前中国汽车行业面临着快速步入汽车社会所带来的能源供应、环境保护等方面的矛盾，汽车产业技术的发展趋向追求更佳驾乘体验的同时，也在朝轻量化和低碳化等方向发展，这促使润滑油技术趋于高端化、低黏化和低碳化，以满足汽车动力性能、燃油经济性以及延长设备使用寿命等方面的需求。</span><span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">中国齿轮行业专家表示：</span><span style=\"font-size:14px;\" lang=\"EN-US\">“</span><span style=\"font-size:14px;\">车辆传动系统设计越来越复杂，尤其是变速箱，技术更是日新月异。</span><span style=\"font-size:14px;\" lang=\"EN-US\">”</span><span style=\"font-size:14px;\">这就要求传动系统润滑油紧随技术的发展，顺应传动系统技术的发展趋势。与此同时，传动系统包括离合器、变速器、传动轴、主减速器、差速器及半轴等部分，这使得传动系统的润滑需求更加多样化，而且由于不同汽车企业传动系统的设计各有不同，在润滑油的选用上，不再是一种规格一统天下的局面，这也大大增加了润滑油技术发展的复杂性。</span><span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">中国石化润滑油公司北京研发中心首席工程师水琳表示：</span><span style=\"font-size:14px;\" lang=\"EN-US\">“</span><span style=\"font-size:14px;\">汽车企业越来越重视油品在设备中的使用，希望通过规范用油提升设备使用性能，这使得传动系统用油趋向专业性和专一性。其中的关键在于要满足不同设备的润滑要求，这需要采用不同配方技术，开发相应设备的专用油品，保证汽车良好的动力性能、优秀的驾驶性能和燃油经济性能。</span><span style=\"font-size:14px;\" lang=\"EN-US\">”<o:p></o:p></span></span></span></p>\r\n<p style=\"text-indent:21pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\"><span style=\"font-size:small;\"><span style=\"font-size:14px;\">据悉，润滑油是中国石化与汽车行业开展深度合作的重要驱动引擎，中国石化润滑油公司通过汽车行业技术合作中心，推动科研前置，开发了许多专用润滑油产品。目前，长城润滑油已经成为国内外</span><span style=\"font-size:14px;\" lang=\"EN-US\">90%</span><span style=\"font-size:14px;\">主流汽车企业的共同选择。在传动系统油品方面，中国石化润滑油公司通过同步开发、同步设计的策略，与一汽、上汽、奇瑞、丰田、日产、大众等国内外多家车厂和变速箱厂建立了紧密的合作，进行联合研发和专用配套产品的开发，取得了不俗的业绩。</span><span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p><span style=\"font-family:宋体;font-size:14px;mso-ascii-theme-font:minor-fareast;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:\'Times New Roman\';mso-bidi-theme-font:minor-bidi;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;\">&nbsp;&nbsp;&nbsp;&nbsp;目前，随着汽车保有量的快速增长以及私人轿车大量进入家庭，汽车在给人们生活带来便利的同时，也给能源、环保、交通等方面等来巨大的压力，这有赖于汽车产业相关各方的共同努力。此次中国石化汽车行业技术合作中心举办<span style=\"font-size:14px;\" lang=\"EN-US\">“</span>车辆传动系统暨车用油技术发展趋势<span style=\"font-size:14px;\" lang=\"EN-US\">”</span>研讨会，加强与汽车行业的技术研讨和交流，探索润滑技术与汽车技术协调发展的路径，是服务汽车社会与产业未来的典范举动。</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '润滑油', '润滑油', '20', '2011-10-31 10:28:24', '2011-10-31 22:36:13', null);
INSERT INTO `kp_information_column` VALUES ('78', '汽配热点', '锦湖：如事故确因轮胎造成愿担责', '立配网', '', '立配网', '已发布', '2011-10-31 10:47:53', '', '<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">“无论调查结果如何，我们都会主动公布，并承担该有的后果。”针对全国政协委员、财政部财政科学研究所所长、经济学家贾康在微博上提出的控诉，锦湖</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">(</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">中国</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">)</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">市场企划科科长赵华侨称，锦湖非常重视这一事件，并将积极配合调查，目前已经和车主取得联系，但由于车主需要静养，还未能正式见面。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;mso-no-proof:yes;\" lang=\"EN-US\"><!--?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /--><v:shapetype id=\"_x0000_t75\" stroked=\"f\" filled=\"f\" path=\"m@4@5l@4@11@9@11@9@5xe\" o:preferrelative=\"t\" o:spt=\"75\" coordsize=\"21600,21600\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:connecttype=\"rect\" gradientshapeok=\"t\" o:extrusionok=\"f\"></v:path><o:lock aspectratio=\"t\" v:ext=\"edit\"></o:lock></v:shapetype></span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"center\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img alt=\"\" src=\"/kindeditor/attached/image/20111031/20111031032956_59619.jpg\" /></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">赵华侨表示，尽管目前还没有调查结果，是否属于锦湖责任还不是很明确，听闻有关部门已经介入调查，但暂时仍未与锦湖方面联系，锦湖也很想积极配合调查此事，给公众一个说法。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">锦湖</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">(</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">中国</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">)</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">方面称，如事故确因锦湖轮胎造成，锦湖轮胎愿意承担相应的责任。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">9</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">月</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">3</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">日，贾康发表微博，称其于</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">9</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">月</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">1</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">日下午遭遇车祸。并称，在保险公司现场勘察左侧前后爆胎发现，轮胎为人们质疑多时的锦湖轮胎，将</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">“</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">在病床上陆续发</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">‘</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">驾车遇祸</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">’</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">系列微博，控诉锦湖轮胎</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\">”</span><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">。</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;margin:0cm 0cm 0pt;mso-pagination:widow-orphan;mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-size:small;\"><span style=\"font-family:宋体;color:black;font-size:14px;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Arial;mso-hansi-font-family:Arial;mso-bidi-font-family:Arial;mso-font-kerning:0pt;\">贾康昨日再发微博：</span><span style=\"font-family:\'Arial\',\'sans-serif\';color:black;mso-bidi-font-size:10.5pt;mso-fareast-font-family:宋体;mso-font-kerning:0pt;\" lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-align:left;line-height:18pt;text-indent:21pt;