-- View structure for `kp_vactivity_comment`
-- ----------------------------
DROP VIEW IF EXISTS `kp_vactivity_comment`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `kp_vactivity_comment` AS select `t2`.`id` AS `id`,`t1`.`nick_name` AS `nick_name`,`t2`.`title` AS `title`,`t2`.`content` AS `content`,`t2`.`insert_time` AS `insert_time`,`t2`.`activity_id` AS `activity_id`,`t1`.`user_name` AS `user_name`,`t3`.`title` AS `activity_title`,`t2`.`user_id` AS `user_id` from ((`kp_user` `t1` join `kp_activity_comment` `t2`) join `kp_activity` `t3`) where ((`t1`.`id` = `t2`.`user_id`) and (`t2`.`activity_id` = `t3`.`id`));

-- ----------------------------
-- View structure for `kp_vhot_tags`
-- ----------------------------
DROP VIEW IF EXISTS `kp_vhot_tags`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `kp_vhot_tags` AS select `t`.`tag_name` AS `tag_name`,count(1) AS `num` from `kp_vtable_tags` `t` group by `t`.`tag_name`;

-- ----------------------------
-- View structure for `kp_vmessage`
-- ----------------------------
DROP VIEW IF EXISTS `kp_vmessage`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `kp_vmessage` AS select `t1`.`id` AS `id`,`t1`.`title` AS `title`,`t1`.`content` AS `content`,`t1`.`sender_id` AS `sender_id`,`t1`.`recever_id` AS `recever_id`,`t1`.`send_status` AS `send_status`,`t1`.`receve_status` AS `receve_status`,`t1`.`read_status` AS `read_status`,`t1`.`insert_time` AS `insert_time`,`t2`.`user_name` AS `sender_name`,`t2`.`true_name` AS `sender_true_name`,`t3`.`user_name` AS `recever_name`,`t3`.`true_name` AS `recever_true_name` from ((`kp_message` `t1` join `kp_user` `t2`) join `kp_user` `t3`) where ((`t1`.`sender_id` = `t2`.`id`) and (`t1`.`recever_id` = `t3`.`id`)) union select `t4`.`id` AS `id`,`t4`.`title` AS `title`,`t4`.`content` AS `content`,`t4`.`sender_id` AS `sender_id`,`t4`.`recever_id` AS `recever_id`,`t4`.`send_status` AS `send_status`,`t4`.`receve_status` AS `receve_status`,`t4`.`read_status` AS `read_status`,`t4`.`insert_time` AS `insert_time`,`t5`.`user_name` AS `sender_name`,'系统管理员' AS `sender_true_name`,`t6`.`user_name` AS `recever_name`,`t6`.`true_name` AS `recever_true_name` from ((`kp_message` `t4` join `kp_admin_user` `t5`) join `kp_user` `t6`) where ((`t4`.`sender_id` = `t5`.`id`) and (`t4`.`recever_id` = `t6`.`id`));

-- ----------------------------
-- View structure for `kp_vsearch`
-- ----------------------------
DROP VIEW IF EXISTS `kp_vsearch`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `kp_vsearch` AS (select `t`.`title` AS `title`,'立配活动' AS `type`,`t`.`insert_time` AS `insert_time`,concat('/Activity/show?id=',`t`.`id`) AS `link`,`t`.`tags` AS `tags`,`t`.`sponsor` AS `other`,0 AS `seq` from `kp_activity` `t` where (`t`.`status` = '已发布')) union all (select `t`.`title` AS `title`,`t`.`column` AS `type`,`t`.`insert_time` AS `insert_time`,concat('/InformationColumn/supInformationColumnDetail?id=',`t`.`id`) AS `link`,`t`.`search_tags` AS `tags`,'' AS `other`,`t`.`seq` AS `seq` from `kp_information_column` `t` where (`t`.`status` = '已发布'));

-- ----------------------------
-- View structure for `kp_vtable_tags`
-- ----------------------------
DROP VIEW IF EXISTS `kp_vtable_tags`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `kp_vtable_tags` AS select `t2`.`id` AS `id`,`t1`.`tag_name` AS `tag_name`,`t2`.`table_name` AS `table_name`,`t2`.`table_id` AS `table_id` from (`kp_tags` `t1` join `kp_table_tags` `t2`) where (`t1`.`id` = `t2`.`tags_id`);

-- ----------------------------
-- View structure for `kp_vuserlog`
-- ----------------------------
DROP VIEW IF EXISTS `kp_vuserlog`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `kp_vuserlog` AS select `t2`.`id` AS `id`,`t1`.`nick_name` AS `nick_name`,`t1`.`user_name` AS `user_name`,`t3`.`sponsor` AS `sponsor`,`t2`.`insert_time` AS `insert_time`,`t3`.`title` AS `title`,`t2`.`act_describ` AS `act_describ`,`t1`.`id` AS `user_id`,`t3`.`type` AS `type` from ((`kp_user` `t1` join `kp_userlog` `t2`) join `kp_activity` `t3`) where ((`t1`.`id` = `t2`.`user_id`) and (`t2`.`table_name` = 'kp_activity') and (`t3`.`id` = `t2`.`table_id`));
