select `t2`.`id` AS `id`,`t1`.`nick_name` AS `nick_name`,`t2`.`title` AS `title`,`t2`.`content` AS `content`,`t2`.`insert_time` AS `insert_time`,`t2`.`activity_id` AS `activity_id`,`t1`.`user_name` AS `user_name`,`t3`.`title` AS `activity_title` from ((`kp_user` `t1` join `kp_activity_comment` `t2`) join `kp_activity` `t3`) where ((`t1`.`id` = `t2`.`user_id`) and (`t2`.`activity_id` = `t3`.`id`))
